import {
  __verseCache,
  VERSE_CACHE_LIMIT,
  clearCache,
  // Exported for testing
  getVerseWithCache,
} from '../[folderId]/hooks/useBookmarkFolderData';

import type { Verse } from '@/types';

jest.mock('@/lib/api', () => ({
  getVerseByKey: jest.fn(async (key: string) => ({ verse_key: key }) as Verse),
  getVerseById: jest.fn(async (id: string) => ({ verse_key: id }) as Verse),
}));

describe('verse cache', () => {
  beforeEach(() => {
    clearCache();
  });

  it('evicts oldest entry when cache limit exceeded', async () => {
    for (let i = 0; i < VERSE_CACHE_LIMIT; i++) {
      await getVerseWithCache(String(i), 1);
    }

    expect(__verseCache.size).toBe(VERSE_CACHE_LIMIT);

    await getVerseWithCache('overflow', 1);

    expect(__verseCache.size).toBe(VERSE_CACHE_LIMIT);
    expect(__verseCache.has('0-1')).toBe(false);
    expect(__verseCache.has('overflow-1')).toBe(true);
  });
});

'use client';

import { useRouter } from 'next/navigation';
import React, { memo, useCallback } from 'react';

import { useBookmarks } from '@/app/providers/BookmarkContext';
import { useSettings } from '@/app/providers/SettingsContext';
import { LoadingError } from '@/app/shared/LoadingError';
import { ContentBookmarkCard } from '@/app/shared/ui/cards/ContentBookmarkCard';
import { Bookmark } from '@/types';

import { useBookmarkAudio } from '../hooks/useBookmarkAudio';
import { useBookmarkVerse } from '../hooks/useBookmarkVerse';
import { ErrorFallback, LoadingFallback } from './shared/BookmarkCardComponents';

interface BookmarkCardProps {
  bookmark: Bookmark;
  folderId: string;
  onRemove?: () => void;
}

const useBookmarkHandlers = (
  enrichedBookmark: Bookmark,
  folderId: string,
  onRemove?: () => void
): {
  handleRemoveBookmark: () => void;
  handleNavigateToVerse: () => void;
  isVerseBookmarked: boolean;
} => {
  const { removeBookmark, isBookmarked } = useBookmarks();
  const router = useRouter();

  const handleRemoveBookmark = useCallback((): void => {
    removeBookmark(enrichedBookmark.verseId, folderId);
    onRemove?.();
  }, [removeBookmark, enrichedBookmark.verseId, folderId, onRemove]);

  const handleNavigateToVerse = useCallback((): void => {
    if (!enrichedBookmark.verseKey) return;
    const [surahId] = enrichedBookmark.verseKey.split(':');
    router.push(`/surah/${surahId}#verse-${enrichedBookmark.verseId}`);
  }, [enrichedBookmark.verseKey, enrichedBookmark.verseId, router]);

  const isVerseBookmarked = isBookmarked(enrichedBookmark.verseId);

  return { handleRemoveBookmark, handleNavigateToVerse, isVerseBookmarked };
};

const useBookmarkCardState = (
  enrichedBookmark: Bookmark,
  isLoading: boolean
): { isDataLoading: boolean } => {
  const isDataLoading =
    isLoading ||
    !enrichedBookmark.verseText ||
    !enrichedBookmark.translation ||
    !enrichedBookmark.verseKey ||
    !enrichedBookmark.surahName ||
    !enrichedBookmark.verseApiId;

  return { isDataLoading };
};

export const BookmarkCard = memo(function BookmarkCard({
  bookmark,
  folderId,
  onRemove,
}: BookmarkCardProps): React.JSX.Element {
  const { settings } = useSettings();
  const { chapters } = useBookmarks();
  const { bookmark: enrichedBookmark, isLoading, error } = useBookmarkVerse(bookmark, chapters);

  const { handlePlayPause, isPlaying, isLoadingAudio } = useBookmarkAudio(
    enrichedBookmark,
    enrichedBookmark.verseApiId
  );

  const { handleRemoveBookmark, handleNavigateToVerse, isVerseBookmarked } = useBookmarkHandlers(
    enrichedBookmark,
    folderId,
    onRemove
  );

  const { isDataLoading } = useBookmarkCardState(enrichedBookmark, isLoading);

  return (
    <LoadingError
      isLoading={isDataLoading}
      error={error}
      loadingFallback={<LoadingFallback />}
      errorFallback={<ErrorFallback error={error} verseId={bookmark.verseId} />}
    >
      <ContentBookmarkCard
        bookmark={enrichedBookmark}
        isPlaying={isPlaying}
        isLoadingAudio={isLoadingAudio}
        onPlayPause={handlePlayPause}
        isBookmarked={isVerseBookmarked}
        onBookmark={handleRemoveBookmark}
        onNavigateToVerse={handleNavigateToVerse}
        settings={{
          arabicFontFace: settings.arabicFontFace,
          arabicFontSize: settings.arabicFontSize,
          tajweed: settings.tajweed,
        }}
      />
    </LoadingError>
  );
});

'use client';

import React, { useState } from 'react';

import { useBookmarks } from '@/app/providers/BookmarkContext';
import { Bookmark, Folder } from '@/types';

import { useFolderNavigation } from '../hooks/useFolderNavigation';
import { FolderItem } from './shared/folder';

interface BookmarkFolderContentProps {
  bookmarks: Bookmark[];
  folder: Folder;
  activeVerseId?: string;
  onVerseSelect?: (verseId: string) => void;
}

export const BookmarkFolderContent = ({
  bookmarks,
  folder,
  activeVerseId,
  onVerseSelect,
}: BookmarkFolderContentProps): React.JSX.Element => {
  const { folders } = useBookmarks();
  const [expandedFolderId, setExpandedFolderId] = useState<string | null>(folder.id);

  const { handleFolderSelect } = useFolderNavigation(folder.id);

  const toggleFolder = (folderId: string): void => {
    setExpandedFolderId((currentId) => (currentId === folderId ? null : folderId));
  };

  const FolderListItem = ({ folderItem }: { folderItem: Folder }): React.JSX.Element => {
    const isExpanded = expandedFolderId === folderItem.id;
    const isCurrentFolder = folderItem.id === folder.id;
    const folderBookmarks = isCurrentFolder ? bookmarks : folderItem.bookmarks;

    return (
      <FolderItem
        key={folderItem.id}
        folderItem={folderItem}
        isExpanded={isExpanded}
        isCurrentFolder={isCurrentFolder}
        folderBookmarks={folderBookmarks}
        onToggle={toggleFolder}
        onSelect={handleFolderSelect}
        activeVerseId={activeVerseId}
        onVerseSelect={onVerseSelect}
      />
    );
  };

  return (
    <div className="flex-1 p-4">
      <div className="space-y-3">
        {folders.map((folderItem) => (
          <FolderListItem key={folderItem.id} folderItem={folderItem} />
        ))}
      </div>
    </div>
  );
};

'use client';

import { motion } from 'framer-motion';
import React from 'react';

import { Folder, Bookmark } from '@/types';

import { VerseList } from './bookmark-list-view/VerseList';
import {
  BookmarkListContent,
  BookmarkListHeader,
  EmptyBookmarkState,
} from './shared/BookmarkListComponents';
import { useBookmarkListState } from '../hooks/useBookmarkListState';

interface BookmarkListViewProps {
  folder: Folder;
  onBack?: () => void;
  bookmarks?: Bookmark[];
  showAsVerseList?: boolean;
}

export const BookmarkListView = ({
  folder,
  onBack,
  bookmarks: externalBookmarks,
  showAsVerseList = false,
}: BookmarkListViewProps): React.JSX.Element => {
  const { bookmarks, listHeight, handleRemoveBookmark } = useBookmarkListState(
    folder,
    externalBookmarks
  );

  if (showAsVerseList) {
    return (
      <VerseList
        bookmarks={bookmarks}
        folder={folder}
        listHeight={listHeight}
        onRemoveBookmark={handleRemoveBookmark}
      />
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0, x: 20 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: -20 }}
      transition={{ duration: 0.3 }}
      className="max-w-4xl mx-auto"
    >
      <BookmarkListHeader folder={folder} bookmarkCount={bookmarks.length} onBack={onBack} />
      {bookmarks.length > 0 ? (
        <BookmarkListContent
          bookmarks={bookmarks}
          folder={folder}
          listHeight={listHeight}
          itemSize={180}
          onRemoveBookmark={handleRemoveBookmark}
        />
      ) : (
        <EmptyBookmarkState onBack={onBack} />
      )}
    </motion.div>
  );
};

