  const [isWordPanelOpen, setIsWordPanelOpen] = useState(false);
  const { isHidden } = useHeaderVisibility();

  const {
    juz,
    juzError,
    isLoading,
    error,
    verses,
    isValidating,
    isReachingEnd,
    loadMoreRef,
    translationOptions,
    wordLanguageOptions,
