    activeVerse,
    reciter,
    isPlayerVisible,
    handleNext,
    handlePrev,
  } = useJuzData(juzId);

  const { selectedTranslationName, selectedWordLanguageName } = useSelectedNames({
    settings,
    translationOptions,
    wordLanguageOptions,
    t,
  });

  const { track } = useCoverAndTrack(activeVerse, reciter);

  const contentProps = {
    juzId,
    isLoading,
    error,
    juzError,
    juz,
    verses,
    loadMoreRef,
    isValidating,
    isReachingEnd,
