  onWordLanguagePanelOpen?: () => void;
  onTafsirPanelOpen?: () => void;
  selectedTranslationName?: string;
  selectedTafsirName?: string;
  selectedWordLanguageName?: string;
  showTafsirSetting: boolean;
}

export function buildContentWrapperProps(
  baseProps: Pick<
    SettingsSidebarProps,
    | 'onTranslationPanelOpen'
    | 'onTafsirPanelOpen'
    | 'onWordLanguagePanelOpen'
    | 'selectedTranslationName'
    | 'selectedTafsirName'
    | 'selectedWordLanguageName'
    | 'showTafsirSetting'
  >,
  stateProps: {
    activeTab: string;
    onTabChange: (tab: string) => void;
    tabOptions: Array<{ id: string; label: string }>;
    openSections: Record<string, boolean>;
    onSectionToggle: (section: string) => void;
    onArabicFontPanelOpen: () => void;
  }
): SettingsContentWrapperPropsConfig {
  const coreProps = {
    ...stateProps,
    onTranslationPanelOpen: baseProps.onTranslationPanelOpen,
    onWordLanguagePanelOpen: baseProps.onWordLanguagePanelOpen,
    selectedTranslationName: baseProps.selectedTranslationName,
    selectedWordLanguageName: baseProps.selectedWordLanguageName,
    showTafsirSetting: baseProps.showTafsirSetting,
  };

  // Add optional tafsir props conditionally
  if (baseProps.onTafsirPanelOpen !== undefined) {
    return {
      ...coreProps,
      onTafsirPanelOpen: baseProps.onTafsirPanelOpen,
      selectedTafsirName: baseProps.selectedTafsirName,
    };
  }

  return coreProps;
}

import { SettingsSidebarProps } from '../types';

export interface SettingsPanelsPropsConfig {
  isArabicFontPanelOpen: boolean;
  onArabicFontPanelClose: () => void;
  isTranslationPanelOpen: boolean;
  onTranslationPanelClose?: () => void;
  isTafsirPanelOpen: boolean;
  onTafsirPanelClose?: () => void;
  isWordLanguagePanelOpen: boolean;
  onWordLanguagePanelClose?: () => void;
}

export function buildPanelsProps(
  baseProps: Pick<
    SettingsSidebarProps,
    | 'isTranslationPanelOpen'
    | 'onTranslationPanelClose'
    | 'isTafsirPanelOpen'
    | 'onTafsirPanelClose'
    | 'isWordLanguagePanelOpen'
    | 'onWordLanguagePanelClose'
  >,
  localState: {
    isArabicFontPanelOpen: boolean;
    onArabicFontPanelClose: () => void;
  }
): SettingsPanelsPropsConfig {
  const coreProps = {
    ...localState,
    isTranslationPanelOpen: baseProps.isTranslationPanelOpen,
    isTafsirPanelOpen: baseProps.isTafsirPanelOpen,
    isWordLanguagePanelOpen: baseProps.isWordLanguagePanelOpen,
  };

  // Add optional close handlers conditionally
  const conditionalProps: Partial<SettingsPanelsPropsConfig> = {};

  if (baseProps.onTranslationPanelClose !== undefined) {
    conditionalProps.onTranslationPanelClose = baseProps.onTranslationPanelClose;
  }

  if (baseProps.onTafsirPanelClose !== undefined) {
    conditionalProps.onTafsirPanelClose = baseProps.onTafsirPanelClose;
  }

  if (baseProps.onWordLanguagePanelClose !== undefined) {
    conditionalProps.onWordLanguagePanelClose = baseProps.onWordLanguagePanelClose;
  }

  return { ...coreProps, ...conditionalProps };
}

