  folders: Folder[];
  setFolders: React.Dispatch<React.SetStateAction<Folder[]>>;
  pinned: Bookmark[];
  setPinned: React.Dispatch<React.SetStateAction<Bookmark[]>>;
  chapters: Chapter[];
  fetchMetadata: (verseId: string, chaptersList: Chapter[]) => void;
}

export default function useBookmarkOperations({
  folders,
  setFolders,
  pinned: _pinned,
  setPinned,
  chapters,
  fetchMetadata,
}: UseBookmarkOperationsOptions): BookmarkOperations {
  // Mark intentionally unused option to satisfy linter while preserving signature
  void _pinned;
  return useMemo(() => {
    function addBookmark(verseId: string, folderId?: string): void {
      if (folderId === 'pinned') {
        setPinned((p) =>
          p.some((b) => b.verseId === verseId) ? p : [...p, { verseId, createdAt: Date.now() }]
        );
        void fetchMetadata(verseId, chapters);
        return;
      }
      setFolders((p) => addBookmarkToFolder(p, verseId, folderId));
      void fetchMetadata(verseId, chapters);
    }
    function removeBookmark(verseId: string, folderId: string): void {
      if (folderId === 'pinned') {
        setPinned((p) => p.filter((b) => b.verseId !== verseId));
        return;
      }
      setFolders((p) => removeBookmarkFromFolder(p, verseId, folderId));
    }
    function toggleBookmark(verseId: string, folderId?: string): void {
      if (isVerseBookmarked(folders, verseId)) {
        const f = findBookmarkInFolders(folders, verseId);
        if (f) removeBookmark(verseId, f.folder.id);
      } else {
        addBookmark(verseId, folderId);
      }
    }
    function updateBookmark(verseId: string, data: Partial<Bookmark>): void {
      setFolders((p) => updateBookmarkInFolders(p, verseId, data));
      setPinned((p) => p.map((b) => (b.verseId === verseId ? { ...b, ...data } : b)));
    }
    return { addBookmark, removeBookmark, toggleBookmark, updateBookmark };
  }, [folders, setFolders, setPinned, chapters, fetchMetadata]);
}

'use client';

import { useCallback } from 'react';

import { Folder } from '@/types';

import { createNewFolder } from '../bookmark-utils';

export interface FolderOperations {
  createFolder: (name: string, color?: string, icon?: string) => void;
  deleteFolder: (folderId: string) => void;
  renameFolder: (folderId: string, newName: string, color?: string, icon?: string) => void;
}

export const useFolderOperations = (
  setFolders: React.Dispatch<React.SetStateAction<Folder[]>>
): FolderOperations => {
  const createFolder = useCallback(
    (name: string, color?: string, icon?: string) => {
      const newFolder = createNewFolder(name, color, icon);
      setFolders((prev) => [...prev, newFolder]);
    },
    [setFolders]
  );

  const deleteFolder = useCallback(
    (folderId: string) => {
      setFolders((prev) => prev.filter((f) => f.id !== folderId));
    },
    [setFolders]
  );

  const renameFolder = useCallback(
    (folderId: string, newName: string, color?: string, icon?: string) => {
      setFolders((prev) =>
        prev.map((folder) =>
          folder.id === folderId
            ? { ...folder, name: newName, ...(color && { color }), ...(icon && { icon }) }
            : folder
        )
      );
    },
    [setFolders]
  );

  return { createFolder, deleteFolder, renameFolder };
};

export default useFolderOperations;

'use client';
import { useCallback } from 'react';

import { MemorizationPlan } from '@/types';

import { createMemorizationPlan, updateMemorizationProgress } from '../bookmark-utils';
export interface MemorizationOperations {
  addToMemorization(surahId: number, targetVerses?: number): void;
  createMemorizationPlan(surahId: number, targetVerses: number, planName?: string): void;
  updateMemorizationProgress(surahId: number, completedVerses: number): void;
  removeFromMemorization(surahId: number): void;
}
export default function useMemorizationOperations(
  memorization: Record<string, MemorizationPlan>,
  setMemorization: React.Dispatch<React.SetStateAction<Record<string, MemorizationPlan>>>
): MemorizationOperations {
  const addToMemorization = useCallback(
    (surahId: number, targetVerses = 10) => {
      const key = surahId.toString();
      if (memorization[key]) return;
      const plan = createMemorizationPlan(surahId, targetVerses);
      setMemorization((prev) => ({ ...prev, [key]: plan }));
    },
    [memorization, setMemorization]
  );
  const createPlan = useCallback(
    (surahId: number, targetVerses: number, planName?: string) => {
      const key = surahId.toString();
      const plan = createMemorizationPlan(surahId, targetVerses, planName);
      setMemorization((prev) => ({ ...prev, [key]: plan }));
    },
    [setMemorization]
  );
  const updateProgress = useCallback(
    (surahId: number, completed: number) => {
      setMemorization((prev) => updateMemorizationProgress(prev, surahId, completed));
    },
    [setMemorization]
  );
  const removeFromMemorization = useCallback(
    (surahId: number) => {
      const key = surahId.toString();
      setMemorization((prev) => {
        const next = { ...prev };
        delete next[key];
        return next;
      });
    },
    [setMemorization]
  );
  return {
    addToMemorization,
    createMemorizationPlan: createPlan,
    updateMemorizationProgress: updateProgress,
    removeFromMemorization,
  };
}

'use client';

import { Dispatch, useEffect, useReducer, useRef } from 'react';

import { reducer } from '../settingsReducer';
import { defaultSettings, loadSettings, saveSettings } from '../settingsStorage';

import type { Action } from '../settingsReducer';
import type { Settings } from '@/types';

const PERSIST_DEBOUNCE_MS = 300;

interface UsePersistentSettingsReturn {
  settings: Settings;
  dispatch: Dispatch<Action>;
}

export const usePersistentSettings = (): UsePersistentSettingsReturn => {
  const [settings, dispatch] = useReducer(reducer, defaultSettings);
  const timeoutRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const latestSettings = useRef(settings);
  const hasLoadedFromStorage = useRef(false);

  useEffect(() => {
    if (typeof window === 'undefined') return;
    const loaded = loadSettings(defaultSettings);
    hasLoadedFromStorage.current = true;
    dispatch({ type: 'SET_SETTINGS', value: loaded });
    latestSettings.current = loaded;
  }, []);

  useEffect(() => {
    latestSettings.current = settings;
    if (typeof window === 'undefined') return;
    if (!hasLoadedFromStorage.current) return;

    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    timeoutRef.current = setTimeout(() => {
      saveSettings(settings);
      timeoutRef.current = null;
    }, PERSIST_DEBOUNCE_MS);

    return () => {
      if (timeoutRef.current) clearTimeout(timeoutRef.current);
    };
  }, [settings]);

  useEffect(() => {
    return () => {
      if (typeof window === 'undefined') return;
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
        saveSettings(latestSettings.current);
      }
    };
  }, []);

  return { settings, dispatch };
};

