        const bookmarkCount = folder.bookmarks?.length || 0;

        return (
          <motion.button
            key={folder.id}
            onClick={() => onFolderSelect(folder)}
            className={cn(
              'w-full flex items-center gap-4 p-4 rounded-2xl transition-all duration-200 text-left',
              isSelected
