interface ResponsiveImageSources {
  mobile?: string;
  tablet?: string;
  desktop?: string;
  fallback: string;
}

interface ResponsiveBackgroundImageProps {
  src: string | ResponsiveImageSources;
  children: React.ReactNode;
  className?: string;
  overlay?: boolean;
  overlayOpacity?: number;
}

/**
 * Utility component for background images with responsive behavior
 */
export const ResponsiveBackgroundImage = ({
  src,
  children,
  className = '',
  overlay = false,
  overlayOpacity = 0.5,
}: ResponsiveBackgroundImageProps): React.JSX.Element => {
  const { breakpoint } = useResponsiveState();

  const pickBackgroundSource = (
    s: string | ResponsiveImageSources,
    bp: ReturnType<typeof useResponsiveState>['breakpoint']
  ) => {
    if (typeof s === 'string') return s;
    switch (bp) {
      case 'mobile':
        return s.mobile || s.fallback;
      case 'tablet':
        return s.tablet || s.mobile || s.fallback;
      case 'desktop':
      case 'wide':
        return s.desktop || s.tablet || s.fallback;
      default:
        return s.fallback;
    }
  };

  const backgroundImage = pickBackgroundSource(src, breakpoint);

  return (
    <div
      className={`relative bg-cover bg-center bg-no-repeat ${className}`}
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {overlay && (
        <div className="absolute inset-0 bg-surface-overlay" style={{ opacity: overlayOpacity }} />
      )}
      <div className="relative z-10">{children}</div>
    </div>
  );
};

import type { ResponsiveImageSources, ResponsiveImageSizes } from './types';

export const pickImageSource = (
  src: string | ResponsiveImageSources,
  breakpoint: 'mobile' | 'tablet' | 'desktop' | 'wide'
) => {
  if (typeof src === 'string') return src;

  switch (breakpoint) {
    case 'mobile':
      return src.mobile || src.fallback;
    case 'tablet':
      return src.tablet || src.mobile || src.fallback;
    case 'desktop':
    case 'wide':
      return src.desktop || src.tablet || src.fallback;
    default:
      return src.fallback;
  }
};

export const buildSizes = (sizes?: ResponsiveImageSizes | string): string => {
  if (!sizes) return '(max-width: 768px) 100vw, (max-width: 1024px) 50vw, 33vw';
  if (typeof sizes === 'string') return sizes;

  const sizeQueries: string[] = [];
  if (sizes.mobile) sizeQueries.push(`(max-width: 767px) ${sizes.mobile}`);
  if (sizes.tablet) sizeQueries.push(`(max-width: 1023px) ${sizes.tablet}`);
  if (sizes.desktop) sizeQueries.push(`(min-width: 1024px) ${sizes.desktop}`);
  sizeQueries.push(sizes.default);

  return sizeQueries.join(', ');
};

export const buildFallbackSvg = () =>
  'data:image/svg+xml,' +
  encodeURIComponent(
    `<svg width="100" height="100" xmlns="http://www.w3.org/2000/svg">\n` +
      `  <rect width="100%" height="100%" fill="rgb(var(--color-surface))"/>\n` +
      `  <text x="50%" y="50%" text-anchor="middle" dy=".3em" fill="rgb(var(--color-muted))" font-family="system-ui" font-size="14">\n` +
      `    Image\n` +
      `  </text>\n` +
      `</svg>`
  );

import { useResponsiveState } from '@/lib/responsive';

import { buildFallbackSvg, buildSizes, pickImageSource } from './imageUtils';

import type { ResponsiveImageProps } from './types';

export const useResponsiveImage = ({
  src,
  sizes,
  fallback,
  loadingStrategy = 'auto',
}: Pick<ResponsiveImageProps, 'src' | 'sizes' | 'fallback' | 'loadingStrategy'>) => {
  const { variant, breakpoint } = useResponsiveState();

  const resolveLoading = (
    strategy: ResponsiveImageProps['loadingStrategy'],
    v: ReturnType<typeof useResponsiveState>['variant']
  ) => {
    if (strategy === 'auto') return v === 'compact' ? 'eager' : 'lazy';
    return strategy;
  };

  const handleImageError = (e: React.SyntheticEvent<HTMLImageElement>) => {
    if (fallback) {
      e.currentTarget.src = fallback;
    } else {
      e.currentTarget.src = buildFallbackSvg();
    }
  };

  const optimalSource = pickImageSource(src, breakpoint);
  const responsiveSizes = buildSizes(sizes);
  const loading = resolveLoading(loadingStrategy, variant);

  return {
    optimalSource,
    responsiveSizes,
    loading,
    priority: loading === 'eager',
    onError: handleImageError,
  };
};

