                <span>Previous</span>
              </div>
              <div className="flex items-center gap-2">
                <IconChevronRight size={16} />
                <span>Next</span>
              </div>

              {/* Vertical swipes */}
              <div className="flex items-center gap-2 col-span-2 justify-center">
                <IconArrowDown size={16} />
                <span>Swipe down for Quran selector</span>
              </div>
