'use client';
import React, { createContext, useContext, useMemo, useRef, useState } from 'react';

import { Verse } from '@/types';

import { usePersistedAudioSettings } from '../hooks/usePersistedAudioSettings';
import { usePlayerVisibility } from '../hooks/usePlayerVisibility';

import type { Reciter, RepeatOptions } from '@/app/shared/player/types';

interface AudioContextType {
  playingId: number | null;
  setPlayingId: React.Dispatch<React.SetStateAction<number | null>>;
  isPlaying: boolean;
  setIsPlaying: React.Dispatch<React.SetStateAction<boolean>>;
  loadingId: number | null;
  setLoadingId: React.Dispatch<React.SetStateAction<number | null>>;
  activeVerse: Verse | null;
  setActiveVerse: React.Dispatch<React.SetStateAction<Verse | null>>;
  audioRef: React.MutableRefObject<HTMLAudioElement | null>;
  repeatOptions: RepeatOptions;
  setRepeatOptions: React.Dispatch<React.SetStateAction<RepeatOptions>>;
  reciter: Reciter;
  setReciter: React.Dispatch<React.SetStateAction<Reciter>>;
  volume: number;
  setVolume: React.Dispatch<React.SetStateAction<number>>;
  playbackRate: number;
  setPlaybackRate: React.Dispatch<React.SetStateAction<number>>;
  isPlayerVisible: boolean;
  openPlayer: () => void;
  closePlayer: () => void;
}

const AudioContext = createContext<AudioContextType | undefined>(undefined);

/**
 * Provides global audio playback state.
 * Wrap your application with this provider to share the currently
 * playing and loading audio identifiers across components.
 */
export const AudioProvider = ({ children }: { children: React.ReactNode }): React.JSX.Element => {
  const [playingId, setPlayingId] = useState<number | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [loadingId, setLoadingId] = useState<number | null>(null);
  const [activeVerse, setActiveVerse] = useState<Verse | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const { repeatOptions, setRepeatOptions } = useRepeatState();
  const { reciter, setReciter, volume, setVolume, playbackRate, setPlaybackRate } =
    usePersistedAudioSettings();

  const { isPlayerVisible, openPlayer, closePlayer } = usePlayerVisibility({
    audioRef,
    setIsPlaying,
    setPlayingId,
    setActiveVerse,
  });

  const value = useMemo(
    () => ({
      playingId,
      setPlayingId,
      isPlaying,
      setIsPlaying,
      loadingId,
      setLoadingId,
      activeVerse,
      setActiveVerse,
      audioRef,
      repeatOptions,
      setRepeatOptions,
      reciter,
      setReciter,
      volume,
      setVolume,
      playbackRate,
      setPlaybackRate,
      isPlayerVisible,
      openPlayer,
      closePlayer,
    }),
    [
      playingId,
      setPlayingId,
      isPlaying,
      setIsPlaying,
      loadingId,
      setLoadingId,
      activeVerse,
      setActiveVerse,
      repeatOptions,
      setRepeatOptions,
      reciter,
      setReciter,
      volume,
      setVolume,
      playbackRate,
      setPlaybackRate,
      isPlayerVisible,
      openPlayer,
      closePlayer,
    ]
  );

  return <AudioContext.Provider value={value}>{children}</AudioContext.Provider>;
};

function useRepeatState(): {
  repeatOptions: RepeatOptions;
  setRepeatOptions: React.Dispatch<React.SetStateAction<RepeatOptions>>;
} {
  const [repeatOptions, setRepeatOptions] = useState<RepeatOptions>({
    mode: 'off',
    start: 1,
    end: 1,
    playCount: 1,
    repeatEach: 1,
    delay: 0,
  });
  return { repeatOptions, setRepeatOptions } as const;
}

/**
 * Hook for accessing audio playback state.
 * Use within components to read or update the current playing or
 * loading audio identifiers managed by `AudioProvider`.
 */
export const useAudio = (): AudioContextType => {
  const ctx = useContext(AudioContext);
  if (!ctx) throw new Error('useAudio must be used within AudioProvider');
  return ctx;
};

import { useCallback, useEffect, useRef, useState, MutableRefObject } from 'react';

import { logger } from '@/src/infrastructure/monitoring/Logger';

type Options = {
  src?: string;
  defaultDuration?: number;
  onTimeUpdate?: (time: number) => void;
  onLoadedMetadata?: (duration: number) => void;
  onError?: (error: unknown) => void;
};

interface AudioPlayerReturn {
  audioRef: MutableRefObject<HTMLAudioElement | null>;
  isPlaying: boolean;
  play: () => void;
  pause: () => void;
  seek: (sec: number) => number;
  setVolume: (vol: number) => void;
  setPlaybackRate: (rate: number) => void;
}

/**
 * Controls an `HTMLAudioElement` and exposes playback helpers.
 *
 * @param options optional callbacks and src.
 * @returns helpers and state for audio playback.
 */
export function useAudioPlayer(options: Options = {}): AudioPlayerReturn {
  const { src, defaultDuration, onTimeUpdate, onLoadedMetadata, onError } = options;
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const play = useCallback(() => {
    const a = audioRef.current;
    if (!a) return;
    a.play().catch((err) => {
      if (err instanceof DOMException && err.name === 'AbortError') return;
      if (onError) onError(err);
      else logger.error(err);
    });
    setIsPlaying(true);
  }, [onError]);

  const pause = useCallback(() => {
    const a = audioRef.current;
    if (!a) return;
    a.pause();
    setIsPlaying(false);
  }, []);

  const seek = useCallback(
    (sec: number) => {
      const a = audioRef.current;
      if (!a) return 0;
      const max = a.duration || defaultDuration || 0;
      a.currentTime = Math.max(0, Math.min(max, sec));
      const t = a.currentTime || 0;
      onTimeUpdate?.(t);
      return t;
    },
    [defaultDuration, onTimeUpdate]
  );

  const setVolume = useCallback((vol: number) => {
    const a = audioRef.current;
    if (!a) return;
    a.volume = vol;
  }, []);

  const setPlaybackRate = useCallback((rate: number): void => {
    const a = audioRef.current;
    if (!a) return;
    a.playbackRate = rate;
  }, []);

  useEffect(() => {
    const a = audioRef.current;
    if (!a) return;
    if (isPlaying) {
      a.play().catch((err) => {
        if (err instanceof DOMException && err.name === 'AbortError') return;
        if (onError) onError(err);
        else logger.error(err);
      });
    } else {
      a.pause();
    }
  }, [isPlaying, src, onError]);

  useEffect(() => {
    const a = audioRef.current;
    if (!a) return;
    const onTime = (): void => onTimeUpdate?.(a.currentTime || 0);
    const onMeta = (): void => onLoadedMetadata?.(a.duration || defaultDuration || 0);
    a.addEventListener('timeupdate', onTime);
    a.addEventListener('loadedmetadata', onMeta);
    onMeta();
    return () => {
      a.removeEventListener('timeupdate', onTime);
      a.removeEventListener('loadedmetadata', onMeta);
    };
  }, [src, defaultDuration, onTimeUpdate, onLoadedMetadata]);

  return { audioRef, isPlaying, play, pause, seek, setVolume, setPlaybackRate };
}

import { useEffect, useState } from 'react';

import { RECITERS } from '@/lib/audio/reciters';

import type { Reciter } from '../types';

interface UsePersistedAudioSettingsReturn {
  reciter: Reciter;
  setReciter: React.Dispatch<React.SetStateAction<Reciter>>;
  volume: number;
  setVolume: React.Dispatch<React.SetStateAction<number>>;
  playbackRate: number;
  setPlaybackRate: React.Dispatch<React.SetStateAction<number>>;
}

/**
 * Manages audio settings with localStorage persistence.
 * Loads settings on mount and writes updates when they change.
 */
export function usePersistedAudioSettings(): UsePersistedAudioSettingsReturn {
  const [reciter, setReciter] = useState<Reciter>(RECITERS[0]);
  const [volume, setVolume] = useState(0.9);
  const [playbackRate, setPlaybackRate] = useState(1);

  // Load persisted settings on mount
  useEffect(() => {
    if (typeof window === 'undefined') return;
    try {
      const found = loadReciterFromStorage();
      if (found) setReciter(found);

      const vol = loadNumberFromStorage('volume', (n) => n >= 0 && n <= 1);
      if (vol !== null) setVolume(vol);

      const rate = loadNumberFromStorage('playbackRate', (n) => n > 0);
      if (rate !== null) setPlaybackRate(rate);
    } catch {
      // ignore corrupted localStorage entries
    }
  }, []);

  // Persist settings when they change
  useEffect(() => {
    if (typeof window === 'undefined') return;
    try {
      localStorage.setItem('reciterId', String(reciter.id));
      localStorage.setItem('volume', String(volume));
      localStorage.setItem('playbackRate', String(playbackRate));
    } catch {
      // ignore write errors
    }
  }, [reciter.id, volume, playbackRate]);

  return { reciter, setReciter, volume, setVolume, playbackRate, setPlaybackRate };
}

function loadReciterFromStorage(): Reciter | null {
  const reciterId = localStorage.getItem('reciterId');
  if (!reciterId) return null;
  const parsed = Number.parseInt(reciterId, 10);
  const found = RECITERS.find((r) => r.id === parsed) ?? null;
  if (!found) localStorage.removeItem('reciterId');
  return found;
}

function loadNumberFromStorage(
  key: 'volume' | 'playbackRate',
  isValid: (n: number) => boolean
): number | null {
  const raw = localStorage.getItem(key);
  if (raw === null) return null;
  const n = Number.parseFloat(raw);
  if (Number.isNaN(n) || !isValid(n)) {
    localStorage.removeItem(key);
    return null;
  }
  return n;
}

import { useCallback, useEffect, useState, RefObject } from 'react';

import { Verse } from '@/types';

import { useSurahRepeat } from './useSurahRepeat';

import type { RepeatOptions } from '../types';

interface Options {
  audioRef: RefObject<HTMLAudioElement | null>;
  repeatOptions: RepeatOptions;
  activeVerse: Verse | null;
  onNext?: () => boolean;
  onPrev?: () => boolean;
  seek: (s: number) => void;
  play: () => void;
  pause: () => void;
  setIsPlaying: (v: boolean) => void;
  setPlayingId: (v: number | null) => void;
}

/**
 * Returns a callback for handling audio completion based on repeat settings.
 *
 * @param options playback context and controls.
 * @returns function to run when playback ends.
 */
export function usePlaybackCompletion({
  audioRef,
  repeatOptions,
  activeVerse,
  onNext,
  onPrev,
  seek,
  play,
  pause,
  setIsPlaying,
  setPlayingId,
}: Options): () => void {
  const [verseRepeatsLeft, setVerseRepeatsLeft] = useState(repeatOptions.repeatEach ?? 1);
  const [playRepeatsLeft, setPlayRepeatsLeft] = useState(repeatOptions.playCount ?? 1);

  useEffect(() => {
    setVerseRepeatsLeft(repeatOptions.repeatEach ?? 1);
    setPlayRepeatsLeft(repeatOptions.playCount ?? 1);
  }, [activeVerse, repeatOptions.repeatEach, repeatOptions.playCount]);

  const delayMs = (repeatOptions.delay ?? 0) * 1000;

  const handleSurahRepeat = useSurahRepeat({
    verseRepeatsLeft,
    playRepeatsLeft,
    repeatEach: repeatOptions.repeatEach ?? 1,
    delay: delayMs,
    onNext,
    onPrev,
    seek,
    play,
    pause,
    setIsPlaying,
    setPlayingId,
    setVerseRepeatsLeft,
    setPlayRepeatsLeft,
  });

  return useCallback((): void => {
    const mode = repeatOptions.mode;
    const start = repeatOptions.start ?? 1;
    const end = repeatOptions.end ?? start;
    const delay = delayMs;
    const currentAyah = activeVerse ? parseInt(activeVerse.verse_key.split(':')[1], 10) : null;

    if (
      mode === 'single' &&
      handleSingleRepeatPure({
        verseRepeatsLeft,
        playRepeatsLeft,
        repeatEach: repeatOptions.repeatEach ?? 1,
        seek,
        play,
        setVerseRepeatsLeft,
        setPlayRepeatsLeft,
      })
    )
      return;

    if (
      mode === 'range' &&
      handleRangeRepeatPure({
        start,
        end,
        currentAyah,
        delay,
        verseRepeatsLeft,
        repeatEach: repeatOptions.repeatEach ?? 1,
        onNext,
        onPrev,
        seek,
        play,
        setVerseRepeatsLeft,
        setPlayRepeatsLeft,
        playRepeatsLeft,
      })
    )
      return;
    if (mode === 'surah') {
      handleSurahRepeat();
      return;
    }

    finalizePlaybackPure({ onNext, audioRef, pause, setIsPlaying, setPlayingId });
  }, [
    repeatOptions,
    activeVerse,
    delayMs,
    verseRepeatsLeft,
    playRepeatsLeft,
    onNext,
    onPrev,
    seek,
    play,
    pause,
    setIsPlaying,
    setPlayingId,
    handleSurahRepeat,
    audioRef,
  ]);
}

function handleSingleRepeatPure({
  verseRepeatsLeft,
  playRepeatsLeft,
  repeatEach,
  seek,
  play,
  setVerseRepeatsLeft,
  setPlayRepeatsLeft,
}: {
  verseRepeatsLeft: number;
  playRepeatsLeft: number;
  repeatEach: number;
  seek: (s: number) => void;
  play: () => void;
  setVerseRepeatsLeft: (n: number) => void;
  setPlayRepeatsLeft: (n: number) => void;
}): boolean {
  if (verseRepeatsLeft > 1) {
    setVerseRepeatsLeft(verseRepeatsLeft - 1);
    seek(0);
    play();
    return true;
  }
  if (playRepeatsLeft > 1) {
    setPlayRepeatsLeft(playRepeatsLeft - 1);
    setVerseRepeatsLeft(repeatEach);
    seek(0);
    play();
    return true;
  }
  return false;
}

function handleRangeRepeatPure({
  start,
  end,
  currentAyah,
  delay,
  verseRepeatsLeft,
  repeatEach,
  onNext,
  onPrev,
  seek,
  play,
  setVerseRepeatsLeft,
  setPlayRepeatsLeft,
  playRepeatsLeft,
}: {
  start: number;
  end: number;
  currentAyah: number | null;
  delay: number;
  verseRepeatsLeft: number;
  repeatEach: number;
  onNext?: () => boolean;
  onPrev?: () => boolean;
  seek: (s: number) => void;
  play: () => void;
  setVerseRepeatsLeft: (n: number) => void;
  setPlayRepeatsLeft: (n: number) => void;
  playRepeatsLeft: number;
}): boolean {
  if (verseRepeatsLeft > 1) {
    setVerseRepeatsLeft(verseRepeatsLeft - 1);
    seek(0);
    play();
    return true;
  }
  setVerseRepeatsLeft(repeatEach);
  if (currentAyah && currentAyah < end) {
    onNext?.();
    return true;
  }
  if (playRepeatsLeft > 1) {
    setPlayRepeatsLeft(playRepeatsLeft - 1);
    const steps = end - start;
    setTimeout(() => {
      for (let i = 0; i < steps; i++) onPrev?.();
    }, delay);
    return true;
  }
  return false;
}

function finalizePlaybackPure({
  onNext,
  audioRef,
  pause,
  setIsPlaying,
  setPlayingId,
}: {
  onNext?: () => boolean;
  audioRef: RefObject<HTMLAudioElement | null>;
  pause: () => void;
  setIsPlaying: (v: boolean) => void;
  setPlayingId: (v: number | null) => void;
}): void {
  const hasNext = onNext?.() ?? false;
  setTimeout(() => {
    if (!hasNext || !audioRef.current?.src) {
      pause();
      setIsPlaying(false);
      setPlayingId(null);
    }
  }, 0);
}

import { Dispatch, SetStateAction, useEffect, useState } from 'react';

import { RECITERS } from '@/lib/audio/reciters';

import { useAudio } from '../context/AudioContext';

import type { RepeatOptions } from '../types';

/**
 * Local state manager for playback options and reciter selection.
 *
 * @param onClose callback to close the options modal.
 * @returns current selections and helpers to commit changes.
 */
interface UsePlaybackOptionsReturn {
  localReciter: string;
  setLocalReciter: Dispatch<SetStateAction<string>>;
  localRepeat: RepeatOptions;
  setLocalRepeat: Dispatch<SetStateAction<RepeatOptions>>;
  rangeWarning: string | null;
  setRangeWarning: Dispatch<SetStateAction<string | null>>;
  commit: () => void;
}

export function usePlaybackOptions(onClose: () => void): UsePlaybackOptionsReturn {
  const { reciter, setReciter, repeatOptions, setRepeatOptions } = useAudio();
  const [localReciter, setLocalReciter] = useState(reciter.id.toString());
  const [localRepeat, setLocalRepeat] = useState<RepeatOptions>(repeatOptions);
  const [rangeWarning, setRangeWarning] = useState<string | null>(null);

  useEffect(() => {
    setLocalReciter(reciter.id.toString());
  }, [reciter]);

  useEffect(() => {
    setLocalRepeat(repeatOptions);
  }, [repeatOptions]);

  const commit = (): void => {
    if (hasNonIntegerValues(localRepeat)) {
      setRangeWarning('Please enter whole numbers only.');
      return;
    }
    const newReciter = RECITERS.find((r) => r.id.toString() === localReciter);
    if (newReciter) setReciter(newReciter);
    const { start, end, adjusted } = adjustRange(localRepeat);
    if (adjusted) {
      setRangeWarning('Start and end values adjusted to a valid range.');
      setLocalRepeat({ ...localRepeat, start, end });
      return;
    }
    setRangeWarning(null);
    setRepeatOptions({ ...localRepeat, start, end });
    onClose();
  };

  return {
    localReciter,
    setLocalReciter,
    localRepeat,
    setLocalRepeat,
    rangeWarning,
    setRangeWarning,
    commit,
  };
}

function hasNonIntegerValues(opts: RepeatOptions): boolean {
  const numericKeys: (keyof RepeatOptions)[] = ['start', 'end', 'playCount', 'repeatEach', 'delay'];
  return numericKeys.some((key) => {
    const val = opts[key];
    return val !== undefined && !Number.isInteger(val);
  });
}

function adjustRange(opts: RepeatOptions): { start: number; end: number; adjusted: boolean } {
  const start = Math.max(1, opts.start ?? 1);
  const end = Math.max(start, opts.end ?? start);
  return { start, end, adjusted: start !== opts.start || end !== opts.end };
}

import { useEffect, Dispatch, SetStateAction } from 'react';

interface Options {
  current: number;
  duration: number;
  setSeek: (sec: number) => void;
  togglePlay: () => void;
  setVolume: Dispatch<SetStateAction<number>>;
}

/**
 * Registers keyboard shortcuts for the audio player.
 *
 * @param options current playback data and control handlers.
 */
export function usePlayerKeyboard({
  current,
  duration,
  setSeek,
  togglePlay,
  setVolume,
}: Options): void {
  useEffect(() => {
    const onKey = (e: KeyboardEvent) => {
      if ((e.target as HTMLElement)?.closest('input, textarea, [role=slider]')) return;
      if (e.code === 'Space') {
        e.preventDefault();
        togglePlay();
      }
      if (e.key === 'ArrowLeft') setSeek(Math.max(0, current - 5));
      if (e.key === 'ArrowRight') setSeek(Math.min(duration || 0, current + 5));
      if (e.key === 'ArrowUp') setVolume((v) => Math.min(1, round(v + 0.05)));
      if (e.key === 'ArrowDown') setVolume((v) => Math.max(0, round(v - 0.05)));
    };
    window.addEventListener('keydown', onKey);
    return () => window.removeEventListener('keydown', onKey);
  }, [current, duration, setSeek, togglePlay, setVolume]);
}

function round(n: number): number {
  return Math.round(n * 100) / 100;
}

