export function useQuranAudioController({
  track,
  onPrev,
  onNext,
}: Props): QuranAudioControllerReturn {
  const [mobileOptionsOpen, setMobileOptionsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState<'reciter' | 'repeat'>('reciter');

  const {
    isPlayerVisible,
    closePlayer,
    audioRef,
    isPlaying,
    setIsPlaying,
    setPlayingId,
    activeVerse,
    volume,
    setVolume,
    playbackRate,
    repeatOptions,
  } = useAudio();

  const timing = useTrackTiming({ track, volume, playbackRate, contextRef: audioRef });

  const controls = usePlayerControls({
    interactable: timing.interactable,
    isPlaying,
    setIsPlaying,
    setPlayingId,
    activeVerse,
    current: timing.current,
    duration: timing.duration,
    seek: timing.seek,
    setVolume,
    play: timing.play,
    pause: timing.pause,
  });

  const handleEnded = usePlaybackCompletion({
    audioRef: timing.audioRef,
    repeatOptions,
    activeVerse,
    onNext,
    onPrev,
    seek: timing.seek,
    play: timing.play,
    pause: timing.pause,
    setIsPlaying,
    setPlayingId,
  });

  const playerLayoutProps = buildPlayerLayoutProps({
    timing,
    isPlaying,
    onNext,
    onPrev,
    closePlayer,
    setMobileOptionsOpen,
    controls,
  });

  return {
    isPlayerVisible,
    audioRef: timing.audioRef,
    handleEnded,
    playerLayoutProps,
    mobileOptionsOpen,
    setMobileOptionsOpen,
    activeTab,
    setActiveTab,
  } as const;
}

import { useCallback } from 'react';

export function handleSurahRepeat({
  verseRepeatsLeft,
  playRepeatsLeft,
  repeatEach,
  delay,
  onNext,
  onPrev,
  seek,
  play,
  pause,
  setIsPlaying,
  setPlayingId,
  setVerseRepeatsLeft,
  setPlayRepeatsLeft,
}: {
  verseRepeatsLeft: number;
  playRepeatsLeft: number;
  repeatEach: number;
  delay: number;
  onNext?: () => boolean;
  onPrev?: () => boolean;
  seek: (s: number) => void;
  play: () => void;
  pause: () => void;
  setIsPlaying: (v: boolean) => void;
  setPlayingId: (v: number | null) => void;
  setVerseRepeatsLeft: (n: number) => void;
  setPlayRepeatsLeft: (n: number) => void;
}): void {
  if (verseRepeatsLeft > 1) {
    setVerseRepeatsLeft(verseRepeatsLeft - 1);
    seek(0);
    play();
    return;
  }
  setVerseRepeatsLeft(repeatEach);
  const hasNext = onNext?.() ?? false;
  if (hasNext) return;
  if (playRepeatsLeft > 1) {
    setPlayRepeatsLeft(playRepeatsLeft - 1);
    setVerseRepeatsLeft(repeatEach);
    setTimeout(() => {
      let hasPrev = true;
      while (hasPrev) {
        hasPrev = onPrev?.() ?? false;
      }
    }, delay);
    return;
  }
  setTimeout(() => {
    pause();
    setIsPlaying(false);
    setPlayingId(null);
  }, 0);
}

/**
 * Provides a memoized callback for handling surah repeat logic.
 *
 * @param params repeat state and audio controls.
 * @returns function to execute the repeat routine.
 */
export function useSurahRepeat({
  verseRepeatsLeft,
  playRepeatsLeft,
  repeatEach,
  delay,
  onNext,
  onPrev,
  seek,
  play,
  pause,
  setIsPlaying,
  setPlayingId,
  setVerseRepeatsLeft,
  setPlayRepeatsLeft,
}: Parameters<typeof handleSurahRepeat>[0]): () => void {
  return useCallback(
    () =>
      handleSurahRepeat({
        verseRepeatsLeft,
        playRepeatsLeft,
        repeatEach,
        delay,
        onNext,
        onPrev,
        seek,
        play,
        pause,
        setIsPlaying,
        setPlayingId,
        setVerseRepeatsLeft,
        setPlayRepeatsLeft,
      }),
    [
      verseRepeatsLeft,
      playRepeatsLeft,
      repeatEach,
      delay,
      onNext,
      onPrev,
      seek,
      play,
      pause,
      setIsPlaying,
      setPlayingId,
      setVerseRepeatsLeft,
      setPlayRepeatsLeft,
    ]
  );
}

import { useEffect, useMemo, useState, MutableRefObject } from 'react';

import { useAudioPlayer } from './useAudioPlayer';

import type { Track } from '../types';

const mmss = (s: number) =>
  `${Math.floor(s / 60)}:${Math.floor(s % 60)
    .toString()
    .padStart(2, '0')}`;

interface Opts {
  track?: Track | null;
  volume: number;
  playbackRate: number;
  contextRef: MutableRefObject<HTMLAudioElement | null>;
}

interface TrackTimingReturn {
  audioRef: MutableRefObject<HTMLAudioElement | null>;
  play: () => void;
  pause: () => void;
  seek: (sec: number) => void;
  current: number;
  duration: number;
  elapsed: string;
  total: string;
  interactable: boolean;
  title: string;
  artist: string;
  cover: string;
}

export function useTrackTiming({
  track,
  volume,
  playbackRate,
  contextRef,
}: Opts): TrackTimingReturn {
  const [current, setCurrent] = useState(0);
  const [duration, setDuration] = useState(track?.durationSec ?? 0);
  const { audioRef, play, pause, seek, setVolume, setPlaybackRate } = useAudioPlayer({
    src: track?.src,
    defaultDuration: track?.durationSec,
    onTimeUpdate: setCurrent,
    onLoadedMetadata: setDuration,
  });
  useEffect(() => {
    contextRef.current = audioRef.current;
  }, [contextRef, audioRef]);
  useEffect(() => {
    setCurrent(0);
    setDuration(track?.durationSec ?? 0);
  }, [track?.src, track?.durationSec]);
  useEffect(() => {
    setVolume(volume);
  }, [volume, setVolume]);
  useEffect(() => {
    setPlaybackRate(playbackRate);
  }, [playbackRate, setPlaybackRate]);
  const elapsed = useMemo(() => mmss(current), [current]);
  const total = useMemo(() => mmss(duration || 0), [duration]);
  const title = track?.title ?? 'No track selected';
  const artist = track?.artist ?? '';
  const cover =
    track?.coverUrl ||
    "data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='96' height='96'><rect width='100%' height='100%' rx='12' ry='12' fill='%23e5e7eb'/><text x='50%' y='52%' dominant-baseline='middle' text-anchor='middle' font-family='Inter, system-ui, sans-serif' font-size='12' fill='%239ca3af'>No cover</text></svg>";
  const interactable = Boolean(track?.src);
  return {
    audioRef,
    play,
    pause,
    seek,
    current,
    duration,
    elapsed,
    total,
    interactable,
    title,
    artist,
    cover,
  } as const;
}

