import { useEffect, useState } from 'react';

import { getJuzByPage, getSurahByPage, JUZ_START_PAGES } from '@/lib/utils/surah-navigation';

import type { Chapter } from '@/types';

interface Args {
  currentSurahId?: number;
  currentJuzId?: number;
  currentPageId?: number;
  chapters: Chapter[];
}

export const useSelectionSync = ({
  currentSurahId,
  currentJuzId,
  currentPageId,
  chapters,
}: Args) => {
  const [selectedSurahId, setSelectedSurahId] = useState<number | null>(currentSurahId ?? null);
  const [selectedJuzId, setSelectedJuzId] = useState<number | null>(currentJuzId ?? null);
  const [selectedPageId, setSelectedPageId] = useState<number | null>(currentPageId ?? null);

  useEffect(() => {
    if (currentSurahId) {
      setSelectedSurahId(currentSurahId);
      const chapter = chapters.find((c) => c.id === currentSurahId);
      const page = chapter?.pages?.[0] ?? 1;
      setSelectedPageId(page);
      setSelectedJuzId(getJuzByPage(page));
    } else if (currentJuzId) {
      setSelectedJuzId(currentJuzId);
      const page = JUZ_START_PAGES[currentJuzId - 1];
      setSelectedPageId(page);
      const chapter = getSurahByPage(page, chapters);
      if (chapter) setSelectedSurahId(chapter.id);
    } else if (currentPageId) {
      setSelectedPageId(currentPageId);
      const page = currentPageId;
      setSelectedJuzId(getJuzByPage(page));
      const chapter = getSurahByPage(page, chapters);
      if (chapter) setSelectedSurahId(chapter.id);
    }
  }, [currentSurahId, currentJuzId, currentPageId, chapters]);

  return {
    selectedSurahId,
    setSelectedSurahId,
    selectedJuzId,
    setSelectedJuzId,
    selectedPageId,
    setSelectedPageId,
  };
};

import { useRef } from 'react';

import { useSidebar } from '@/app/providers/SidebarContext';
import { useScrollCentering } from '@/lib/hooks/useScrollCentering';
import { useScrollPersistence } from '@/lib/hooks/useScrollPersistence';

type TabKey = 'Surah' | 'Juz' | 'Page';

interface Options {
  activeTab: TabKey;
  selectedSurahId: number | null;
  selectedJuzId: number | null;
  selectedPageId: number | null;
}

export const useSidebarScroll = ({
  activeTab,
  selectedSurahId,
  selectedJuzId,
  selectedPageId,
}: Options) => {
  const scrollRef = useRef<HTMLDivElement>(null);
  const {
    surahScrollTop,
    setSurahScrollTop,
    juzScrollTop,
    setJuzScrollTop,
    pageScrollTop,
    setPageScrollTop,
  } = useSidebar();

  const {
    handleScroll,
    prepareForTabSwitch: persistencePrepare,
    rememberScroll: persistRememberScroll,
  } = useScrollPersistence<TabKey>({
    scrollRef,
    activeTab,
    scrollTops: {
      Surah: surahScrollTop,
      Juz: juzScrollTop,
      Page: pageScrollTop,
    },
    setScrollTops: {
      Surah: setSurahScrollTop,
      Juz: setJuzScrollTop,
      Page: setPageScrollTop,
    },
    storageKeys: {
      Surah: 'surahScrollTop',
      Juz: 'juzScrollTop',
      Page: 'pageScrollTop',
    },
  });

  const { skipNextCentering, prepareForTabSwitch: centeringPrepare } = useScrollCentering<TabKey>({
    scrollRef,
    activeTab,
    selectedIds: {
      Surah: selectedSurahId,
      Juz: selectedJuzId,
      Page: selectedPageId,
    },
    scrollTops: {
      Surah: surahScrollTop,
      Juz: juzScrollTop,
      Page: pageScrollTop,
    },
  });

  const rememberScroll = (tab: TabKey) => {
    persistRememberScroll(tab);
    skipNextCentering(tab);
  };

  const prepareForTabSwitch = (nextTab: TabKey) => {
    persistencePrepare();
    centeringPrepare(nextTab);
  };

  return { scrollRef, handleScroll, prepareForTabSwitch, rememberScroll };
};

export type { TabKey };

