'use client';

import React from 'react';

import { useTheme } from '@/app/providers/ThemeContext';
import { SunIcon, MoonIcon } from '@/app/shared/icons';

import { Button } from './Button';

interface ThemeToggleProps {
  variant?: 'primary' | 'secondary' | 'ghost' | 'tabs' | string;
  size?: 'sm' | 'md' | 'lg' | 'icon';
  className?: string;
}

export const ThemeToggle = ({
  variant = 'ghost',
  className,
}: ThemeToggleProps): React.JSX.Element => {
  const { setTheme } = useTheme();
  const [currentTheme, setCurrentTheme] = React.useState<'light' | 'dark'>('light');

  React.useEffect(() => {
    const isDark = document.documentElement.classList.contains('dark');
    setCurrentTheme(isDark ? 'dark' : 'light');
  }, []);

  const handleThemeChange = (theme: 'light' | 'dark') => {
    setTheme(theme);
    setCurrentTheme(theme);
  };

  // Tab-style variant
  if (variant === 'tabs') {
    return (
      <div
        className={`flex items-center p-1 rounded-full bg-interactive border border-border ${className || ''}`}
      >
        <button
          onClick={() => handleThemeChange('light')}
          className={`flex items-center justify-center px-3 py-2 rounded-full text-sm font-semibold transition-colors ${
            currentTheme === 'light'
              ? 'bg-surface shadow text-foreground'
              : 'text-muted hover:text-foreground hover:bg-surface/50'
          }`}
          aria-label="Light theme"
        >
          <SunIcon className="w-4 h-4" />
        </button>
        <button
          onClick={() => handleThemeChange('dark')}
          className={`flex items-center justify-center px-3 py-2 rounded-full text-sm font-semibold transition-colors ${
            currentTheme === 'dark'
              ? 'bg-surface shadow text-foreground'
              : 'text-muted hover:text-foreground hover:bg-surface/50'
          }`}
          aria-label="Dark theme"
        >
          <MoonIcon className="w-4 h-4" />
        </button>
      </div>
    );
  }

  // Original button style for backward compatibility
  const toggleTheme = () => {
    const isDark = document.documentElement.classList.contains('dark');
    setTheme(isDark ? 'light' : 'dark');
  };

  return (
    <Button
      variant="icon-round"
      size="icon"
      onClick={toggleTheme}
      className={`bg-button-secondary/40 hover:bg-button-secondary-hover/60 ${className || ''}`}
      aria-label="Toggle Theme"
    >
      {currentTheme === 'dark' ? (
        <SunIcon className="w-5 h-5 text-accent" />
      ) : (
        <MoonIcon className="w-5 h-5 text-accent" />
      )}
    </Button>
  );
};

import { motion } from 'framer-motion';
import Link from 'next/link';

import type { AnimationConfig } from '../base-card.config';
import type React from 'react';

interface RenderFramerMotionProps {
  href?: string;
  scroll: boolean;
  baseClasses: string;
  onClick?: React.MouseEventHandler<HTMLDivElement | HTMLAnchorElement>;
  dataActive?: boolean;
  role?: string;
  tabIndex?: number;
  ariaLabel?: string;
  onKeyDown?: (e: React.KeyboardEvent) => void;
  animationConfig: AnimationConfig;
  children: React.ReactNode;
  props: Record<string, unknown>;
}

export function renderFramerMotion({
  href,
  scroll,
  baseClasses,
  onClick,
  dataActive,
  role,
  tabIndex,
  ariaLabel,
  onKeyDown,
  animationConfig,
  children,
  props,
}: RenderFramerMotionProps): React.JSX.Element {
  const MotionComponent = href ? motion(Link) : motion.div;
  const framer = animationConfig.framer!;

  return (
    <MotionComponent
      {...(href ? { href, scroll } : {})}
      className={baseClasses}
      onClick={onClick}
      data-active={dataActive}
      role={role}
      tabIndex={tabIndex}
      aria-label={ariaLabel}
      onKeyDown={onKeyDown}
      initial={framer.initial}
      animate={framer.animate}
      exit={framer.exit}
      transition={framer.transition}
      whileHover={framer.hover}
      {...props}
    >
      {children}
    </MotionComponent>
  );
}

interface RenderCSSProps {
  href?: string;
  scroll: boolean;
  baseClasses: string;
  onClick?: React.MouseEventHandler<HTMLDivElement | HTMLAnchorElement>;
  dataActive?: boolean;
  role?: string;
  tabIndex?: number;
  ariaLabel?: string;
  onKeyDown?: (e: React.KeyboardEvent) => void;
  children: React.ReactNode;
  props: Record<string, unknown>;
}

export function renderCSS({
  href,
  scroll,
  baseClasses,
  onClick,
  dataActive,
  role,
  tabIndex,
  ariaLabel,
  onKeyDown,
  children,
  props,
}: RenderCSSProps): React.JSX.Element {
  if (href) {
    return (
      <Link
        href={href}
        scroll={scroll}
        className={baseClasses}
        onClick={onClick}
        data-active={dataActive}
        {...props}
      >
        {children}
      </Link>
    );
  }

  return (
    <div
      className={baseClasses}
      onClick={onClick}
      data-active={dataActive}
      role={role}
      tabIndex={tabIndex}
      aria-label={ariaLabel}
      onKeyDown={onKeyDown}
      {...props}
    >
      {children}
    </div>
  );
}

import { cn } from '@/lib/utils/cn';

import type { AnimationConfig, CardVariant } from '../base-card.config';

export function buildVariantClasses(
  cardVariant: CardVariant,
  animationConfig: AnimationConfig,
  isActive: boolean
): string {
  return cn(
    cardVariant.padding,
    cardVariant.height,
    cardVariant.borderRadius,
    isActive ? cardVariant.background.active : cardVariant.background.inactive,
    isActive ? cardVariant.shadow.active : cardVariant.shadow.inactive,
    animationConfig.type === 'css' && cardVariant.hover.duration,
    animationConfig.type === 'css' && cardVariant.hover.value,
    'group'
  );
}

