interface UseVerseActionsParams {
  isPlaying: boolean;
  isLoadingAudio: boolean;
  isBookmarked: boolean;
  showRemove: boolean;
  verseKey: string;
  onPlayPause: () => void;
  onBookmark: () => void;
  onShare: () => void;
  onNavigateToVerse?: () => void;
  onClose: () => void;
}

export function useVerseActions({
  isPlaying,
  isLoadingAudio,
  isBookmarked,
  showRemove,
  verseKey,
  onPlayPause,
  onBookmark,
  onShare,
  onNavigateToVerse,
  onClose,
}: UseVerseActionsParams): VerseActionItem[] {
  const handleAction = useCallback(
    (action: () => void) => {
      action();
      onClose();
    },
    [onClose]
  );
  const playPauseIcon: ReactElement = isLoadingAudio
    ? createElement(Spinner, { className: 'h-5 w-5 text-accent' })
    : isPlaying
      ? createElement(PauseIcon, { size: 20 })
      : createElement(PlayIcon, { size: 20 });

  const bookmarkIcon: ReactElement =
    isBookmarked || showRemove
      ? createElement(BookmarkIcon, { size: 20 })
      : createElement(BookmarkOutlineIcon, { size: 20 });

  return [
    {
      label: isPlaying ? 'Pause Audio' : 'Play Audio',
      icon: playPauseIcon,
      onClick: () => handleAction(onPlayPause),
      active: isPlaying,
    },
    {
      label: 'View Tafsir',
      icon: createElement(BookReaderIcon, { size: 20 }),
      onClick: () => {},
      href: `/tafsir/${verseKey.replace(':', '/')}`,
    },
    ...(onNavigateToVerse
      ? [
          {
            label: 'Go to Verse',
            icon: createElement(BookReaderIcon, { size: 20 }),
            onClick: () => handleAction(onNavigateToVerse),
          },
        ]
      : []),
    {
      label: showRemove ? 'Remove Bookmark' : isBookmarked ? 'Remove Bookmark' : 'Add Bookmark',
      icon: bookmarkIcon,
      onClick: () => handleAction(onBookmark),
      active: isBookmarked || showRemove,
    },
    {
      label: 'Share',
      icon: createElement(ShareIcon, { size: 20 }),
      onClick: () => handleAction(onShare),
    },
  ];
}

import { ErrorHandler } from '@/src/infrastructure/errors';
import { logger } from '@/src/infrastructure/monitoring/Logger';

export const defaultShare = () => {
  const url = typeof window !== 'undefined' ? window.location.href : '';
  if (navigator.share) {
    navigator.share({ title: 'Quran', url }).catch((err) => {
      logger.warn('Share failed', undefined, err);
      ErrorHandler.handleSync(err, { logError: false, reportError: false });
    });
  } else if (navigator.clipboard) {
    navigator.clipboard.writeText(url).catch((err) => {
      logger.warn('Share failed', undefined, err);
      ErrorHandler.handleSync(err, { logError: false, reportError: false });
    });
  }
};

