// Minimal mock that satisfies Next's useIntersection requirements
class IntersectionObserverMock {
  constructor(cb) {
    this.cb = cb;
  }
  observe = (el) => {
    if (this.cb) {
      this.cb([{ isIntersecting: true, target: el }]);
    }
  };
  unobserve = () => {};
  disconnect = () => {};
}

