import { logger } from '@/src/infrastructure/monitoring/Logger';
import { Verse, Juz, Surah } from '@/types';

import { getSurahList } from '../chapters';
import { apiFetch } from '../client';
import { normalizeVerse, ApiVerse } from './normalize';

let surahList: Surah[] | null = null;

export function clearSurahListCache(): void {
  surahList = null;
}

interface SearchApiResult {
  verse_key: string;
  verse_id: number;
  text: string;
  translations?: Verse['translations'];
}

export async function searchVerses(query: string): Promise<Verse[]> {
  const data = await apiFetch<{ search?: { results: SearchApiResult[] } }>(
    'search',
    { q: query, size: '20', translations: '20' },
    'Failed to search verses'
  );
  const results: SearchApiResult[] = data.search?.results || [];
  return results.map((r) => ({
    id: r.verse_id,
    verse_key: r.verse_key,
    text_uthmani: r.text,
    translations: r.translations,
  })) as Verse[];
}

export async function getJuz(juzId: string | number): Promise<Juz> {
  const data = await apiFetch<{ juz: Juz }>(`juzs/${juzId}`, {}, 'Failed to fetch juz');
  return data.juz as Juz;
}

export async function getRandomVerse(
  translationId: number,
  rng: () => number = Math.random
): Promise<Verse> {
  try {
    const surahs = surahList ?? (surahList = await getSurahList());
    const randomSurah = surahs[Math.floor(rng() * surahs.length)];
    const randomAyah = Math.floor(rng() * randomSurah.verses) + 1;
    const verseKey = `${randomSurah.number}:${randomAyah}`;
    const data = await apiFetch<{ verse: ApiVerse }>(
      `verses/by_key/${verseKey}`,
      { translations: translationId.toString(), fields: 'text_uthmani' },
      'Failed to fetch random verse'
    );
    return normalizeVerse(data.verse);
  } catch (error) {
    logger.warn('API unavailable, using fallback verse:', undefined, error as Error);
    const { fallbackVerse } = await import('../fallback-verse');
    return fallbackVerse;
  }
}

export async function getVerseById(
  verseId: string | number,
  translationId: number
): Promise<Verse> {
  const data = await apiFetch<{ verse: ApiVerse }>(
    `verses/${verseId}`,
    { translations: translationId.toString(), fields: 'text_uthmani' },
    'Failed to fetch verse'
  );
  return normalizeVerse(data.verse);
}

/**
 * Fetch a single verse by its composite key (e.g., "2:255").
 */
export async function getVerseByKey(verseKey: string, translationId: number): Promise<Verse> {
  const data = await apiFetch<{ verse: ApiVerse }>(
    `verses/by_key/${encodeURIComponent(verseKey)}`,
    { translations: translationId.toString(), fields: 'text_uthmani' },
    'Failed to fetch verse by key'
  );
  return normalizeVerse(data.verse);
}

import { Verse } from '@/types';

import { apiFetch } from '../client';
import { normalizeVerse, ApiVerse } from './normalize';

import type { LanguageCode } from '@/lib/text/languageCodes';

export interface PaginatedVerses {
  verses: Verse[];
  totalPages: number;
}

/**
 * Fetch verses grouped by chapter, juz or page and return pagination info.
 *
 * @param type            Grouping method for the verses.
 * @param id              Identifier for the grouping (chapter/juz/page).
 * @param translationIds  One or more translation IDs.
 * @param page            Page number (1-indexed).
 * @param perPage         Number of verses per page.
 * @param wordLang        Language code for word translations.
 *
 * Handles single vs array translation IDs and normalizes API pagination
 * metadata, defaulting to one page when the server omits totals.
 */
export async function fetchVerses(
  type: 'by_chapter' | 'by_juz' | 'by_page',
  id: string | number,
  translationIds: number | number[],
  page = 1,
  perPage = 20,
  wordLang: string = 'en'
): Promise<PaginatedVerses> {
  const lang = wordLang as LanguageCode;
  const translationIdsArray = Array.isArray(translationIds) ? translationIds : [translationIds];
  const translationParam = translationIdsArray.join(',');

  const data = await apiFetch<{
    verses: ApiVerse[];
    meta?: { total_pages: number };
    pagination?: { total_pages: number };
  }>(
    `verses/${type}/${id}`,
    {
      language: lang,
      words: 'true',
      word_translation_language: lang,
      word_fields: 'text_uthmani',
      translations: translationParam,
      fields: 'text_uthmani,audio',
      per_page: perPage.toString(),
      page: page.toString(),
    },
    'Failed to fetch verses'
  );
  const totalPages = data.meta?.total_pages ?? data.pagination?.total_pages ?? 1;
  return {
    totalPages,
    verses: data.verses.map((v) => normalizeVerse(v, lang)),
  };
}

export function getVersesByChapter(
  chapterId: string | number,
  translationIds: number | number[],
  page = 1,
  perPage = 20,
  wordLang: string = 'en'
): Promise<PaginatedVerses> {
  return fetchVerses('by_chapter', chapterId, translationIds, page, perPage, wordLang);
}

export function getVersesByJuz(
  juzId: string | number,
  translationIds: number | number[],
  page = 1,
  perPage = 20,
  wordLang: string = 'en'
): Promise<PaginatedVerses> {
  return fetchVerses('by_juz', juzId, translationIds, page, perPage, wordLang);
}

export function getVersesByPage(
  pageId: string | number,
  translationIds: number | number[],
  page = 1,
  perPage = 20,
  wordLang: string = 'en'
): Promise<PaginatedVerses> {
  return fetchVerses('by_page', pageId, translationIds, page, perPage, wordLang);
}

import React from 'react';

import { getFocusableElements } from './focusable';

type FocusHandlers = { saveFocus: () => void; restoreFocus: () => void };

export const useFocusTrap = (
  isActive: boolean,
  containerRef: React.RefObject<HTMLElement>
): void => {
  const prev = React.useRef<HTMLElement | null>(null);
  React.useEffect(() => {
    if (!isActive || !containerRef.current) return;
    prev.current = document.activeElement as HTMLElement;
    const focusables = getFocusableElements(containerRef.current);
    if (!focusables.length) return;
    focusables[0].focus();
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key !== 'Tab') return;
      if (focusables.length === 1) {
        e.preventDefault();
        return;
      }
      const first = focusables[0];
      const last = focusables[focusables.length - 1];
      if (e.shiftKey && document.activeElement === first) {
        e.preventDefault();
        last.focus();
      } else if (!e.shiftKey && document.activeElement === last) {
        e.preventDefault();
        first.focus();
      }
    };
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isActive, containerRef]);
  React.useEffect(() => {
    if (!isActive && prev.current) {
      prev.current.focus();
      prev.current = null;
    }
  }, [isActive]);
};

export const useFocusRestoration = (): FocusHandlers => {
  const saved = React.useRef<string | null>(null);
  const saveFocus = React.useCallback(() => {
    const el = document.activeElement as HTMLElement;
    if (el?.id) saved.current = el.id;
  }, []);
  const restoreFocus = React.useCallback(() => {
    if (saved.current) {
      document.getElementById(saved.current)?.focus();
      saved.current = null;
    }
  }, []);
  return { saveFocus, restoreFocus };
};

export const useAutoFocus = (
  shouldFocus: boolean,
  ref: React.RefObject<HTMLElement>,
  delay = 100
): void => {
  React.useEffect(() => {
    if (!shouldFocus || !ref.current) return;
    const t = setTimeout(() => ref.current?.focus(), delay);
    return () => clearTimeout(t);
  }, [shouldFocus, ref, delay]);
};

export const useResponsiveFocus = (
  breakpoint: 'mobile' | 'tablet' | 'desktop',
  enabled = true
): FocusHandlers => {
  const focus = useFocusRestoration();
  React.useEffect(() => {
    if (!enabled) return;
    focus.saveFocus();
    const t = setTimeout(() => focus.restoreFocus(), 150);
    return () => clearTimeout(t);
  }, [breakpoint, focus, enabled]);
  return enabled ? focus : { saveFocus() {}, restoreFocus() {} };
};

export const useRovingTabIndex = (
  items: React.RefObject<HTMLElement>[],
  activeIndex: number,
  onActiveIndexChange: (index: number) => void
): void => {
  React.useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (!['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'Home', 'End'].includes(e.key))
        return;
      e.preventDefault();
      let idx = activeIndex;
      switch (e.key) {
        case 'ArrowUp':
        case 'ArrowLeft':
          idx = activeIndex > 0 ? activeIndex - 1 : items.length - 1;
          break;
        case 'ArrowDown':
        case 'ArrowRight':
          idx = activeIndex < items.length - 1 ? activeIndex + 1 : 0;
          break;
        case 'Home':
          idx = 0;
          break;
        case 'End':
          idx = items.length - 1;
          break;
      }
      onActiveIndexChange(idx);
    };
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [activeIndex, items.length, onActiveIndexChange]);
  React.useEffect(() => {
    items.forEach((ref, i) => {
      if (ref.current) {
        ref.current.tabIndex = i === activeIndex ? 0 : -1;
        if (i === activeIndex) ref.current.focus();
      }
    });
  }, [activeIndex, items]);
};

import { act, renderHook } from '@testing-library/react';

import { useScrollCentering } from '@/lib/hooks/useScrollCentering';

type Tab = 'Surah' | 'Juz' | 'Page';

const makeRect = (top: number, bottom: number): DOMRect => ({
  top,
  bottom,
  left: 0,
  right: 0,
  width: 0,
  height: bottom - top,
  x: 0,
  y: top,
  toJSON: () => ({}),
});

describe('useScrollCentering', () => {
  const scrollRef = { current: document.createElement('div') } as React.RefObject<HTMLDivElement>;

  const createEls = () => {
    const surahEl = document.createElement('div');
    const juzEl = document.createElement('div');
    surahEl.scrollIntoView = jest.fn();
    juzEl.scrollIntoView = jest.fn();
    scrollRef.current!.innerHTML = '';
    scrollRef.current!.appendChild(surahEl);
    scrollRef.current!.appendChild(juzEl);
    return { surahEl, juzEl };
  };

  beforeEach(() => {
    sessionStorage.clear();
    jest.restoreAllMocks();
    scrollRef.current!.innerHTML = '';
  });

  it('centers active element on initial render when outside view', () => {
    const { surahEl } = createEls();
    surahEl.dataset.active = 'true';
    jest.spyOn(scrollRef.current!, 'getBoundingClientRect').mockReturnValue(makeRect(0, 100));
    jest.spyOn(surahEl, 'getBoundingClientRect').mockReturnValue(makeRect(200, 250));
    renderHook(() =>
      useScrollCentering<Tab>({
        scrollRef,
        activeTab: 'Surah',
        selectedIds: { Surah: 1, Juz: null, Page: null },
        scrollTops: { Surah: 0, Juz: 0, Page: 0 },
      })
    );
    expect(surahEl.scrollIntoView).toHaveBeenCalledWith({ block: 'center' });
  });

  it('does not recenter when element is already in view after scrolling', () => {
    const { surahEl } = createEls();
    surahEl.dataset.active = 'true';
    jest.spyOn(scrollRef.current!, 'getBoundingClientRect').mockReturnValue(makeRect(0, 100));
    jest.spyOn(surahEl, 'getBoundingClientRect').mockReturnValue(makeRect(10, 20));
    renderHook(() =>
      useScrollCentering<Tab>({
        scrollRef,
        activeTab: 'Surah',
        selectedIds: { Surah: 1, Juz: null, Page: null },
        scrollTops: { Surah: 50, Juz: 0, Page: 0 },
      })
    );
    expect(surahEl.scrollIntoView).not.toHaveBeenCalled();
  });

  it('centers element after switching tabs', () => {
    const { surahEl, juzEl } = createEls();
    surahEl.dataset.active = 'true';
    jest.spyOn(scrollRef.current!, 'getBoundingClientRect').mockReturnValue(makeRect(0, 100));
    jest.spyOn(juzEl, 'getBoundingClientRect').mockReturnValue(makeRect(200, 250));
    const { result, rerender } = renderHook(
      ({ activeTab }) =>
        useScrollCentering<Tab>({
          scrollRef,
          activeTab,
          selectedIds: { Surah: 1, Juz: 1, Page: null },
          scrollTops: { Surah: 0, Juz: 0, Page: 0 },
        }),
      { initialProps: { activeTab: 'Surah' as Tab } }
    );
    act(() => result.current.prepareForTabSwitch('Juz'));
    delete surahEl.dataset.active;
    juzEl.dataset.active = 'true';
    rerender({ activeTab: 'Juz' });
    expect(juzEl.scrollIntoView).toHaveBeenCalledWith({ block: 'center' });
  });

  it('skipNextCentering prevents centering and clears the session flag', () => {
    const { surahEl } = createEls();
    surahEl.dataset.active = 'true';
    jest.spyOn(scrollRef.current!, 'getBoundingClientRect').mockReturnValue(makeRect(0, 100));
    jest.spyOn(surahEl, 'getBoundingClientRect').mockReturnValue(makeRect(200, 250));
    sessionStorage.setItem('skipCenterSurah', '1');
    renderHook(() =>
      useScrollCentering<Tab>({
        scrollRef,
        activeTab: 'Surah',
        selectedIds: { Surah: 1, Juz: null, Page: null },
        scrollTops: { Surah: 0, Juz: 0, Page: 0 },
      })
    );
    expect(surahEl.scrollIntoView).not.toHaveBeenCalled();
    expect(sessionStorage.getItem('skipCenterSurah')).toBeNull();
