import { RefObject, useCallback, useEffect, useLayoutEffect, useRef } from 'react';

interface ScrollCenteringOptions<T extends string> {
  scrollRef: RefObject<HTMLDivElement | null>;
  activeTab: T;
  selectedIds: Record<T, number | null>;
  scrollTops: Record<T, number>;
}

interface ScrollCenteringResult<T extends string> {
  skipNextCentering: (tab: T) => void;
  prepareForTabSwitch: (nextTab: T) => void;
}

export const useScrollCentering = <T extends string>({
  scrollRef,
  activeTab,
  selectedIds,
  scrollTops,
}: ScrollCenteringOptions<T>): ScrollCenteringResult<T> => {
  const tabs = Object.keys(selectedIds) as T[];
  const shouldCenterRef = useRef<Record<T, boolean>>(
    tabs.reduce((acc, t) => ({ ...acc, [t]: true }), {} as Record<T, boolean>)
  );

  useLayoutEffect(() => {
    tabs.forEach((tab) => {
      if (sessionStorage.getItem(`skipCenter${tab}`) === '1') {
        shouldCenterRef.current[tab] = false;
        sessionStorage.removeItem(`skipCenter${tab}`);
      }
    });
  }, [tabs]);

  const prevIds = useRef<Record<T, number | null>>(
    tabs.reduce((acc, t) => ({ ...acc, [t]: selectedIds[t] }), {} as Record<T, number | null>)
  );

  useEffect(() => {
    tabs.forEach((tab) => {
      const currentId = selectedIds[tab];
      if (prevIds.current[tab] !== currentId) {
        if (activeTab !== tab) {
          shouldCenterRef.current[tab] = true;
        }
        prevIds.current[tab] = currentId;
      }
    });
  }, [activeTab, selectedIds, tabs]);

  useLayoutEffect(() => {
    const container = scrollRef.current;
    if (!container) return;
    const activeEl = container.querySelector<HTMLElement>('[data-active="true"]');
    if (activeEl) {
      const containerRect = container.getBoundingClientRect();
      const activeRect = activeEl.getBoundingClientRect();
      const isOutside =
        activeRect.top < containerRect.top || activeRect.bottom > containerRect.bottom;
      if (shouldCenterRef.current[activeTab] && (scrollTops[activeTab] === 0 || isOutside)) {
        activeEl.scrollIntoView({ block: 'center' });
      }
    }
    shouldCenterRef.current[activeTab] = false;
  }, [activeTab, scrollRef, scrollTops, selectedIds]);

  const skipNextCentering = useCallback((tab: T): void => {
    sessionStorage.setItem(`skipCenter${tab}`, '1');
  }, []);

  const prepareForTabSwitch = useCallback(
    (nextTab: T): void => {
      shouldCenterRef.current[nextTab] = true;
    },
    [shouldCenterRef]
  );

  return { skipNextCentering, prepareForTabSwitch };
};

import { useCallback, useState } from 'react';

import { useDraggableSelection } from './useDraggableSelection';
import { useResourceSearch } from './useResourceSearch';

interface Resource {
  id: number;
  name: string;
  lang: string;
}
interface SelectableOptions<T extends Resource> {
  resources: T[];
  selectionLimit: number;
  initialSelectedIds?: number[];
  languageSort?: (a: string, b: string) => number;
}

export const useSelectableResources = <T extends Resource>({
  resources,
  selectionLimit,
  initialSelectedIds = [],
  languageSort,
}: SelectableOptions<T>) => {
  const [selectedIds, setSelectedIds] = useState(new Set(initialSelectedIds));
  const drag = useDraggableSelection(initialSelectedIds);
  const search = useResourceSearch<T>({ resources, languageSort });

  const setSelections = useCallback(
    (ids: number[]) => {
      setSelectedIds(new Set(ids));
      drag.setOrderedSelection(ids);
    },
    [drag]
  );

  const handleSelectionToggle = useCallback(
    (id: number): boolean => {
      const next = new Set(selectedIds);
      let order = [...drag.orderedSelection];
      let changed = false;
      if (next.has(id)) {
        next.delete(id);
        order = order.filter((i) => i !== id);
        changed = true;
      } else if (next.size < selectionLimit) {
        next.add(id);
        order.push(id);
        changed = true;
      }
      if (changed) {
        setSelectedIds(next);
        drag.setOrderedSelection(order);
      }
      return changed;
    },
    [drag, selectedIds, selectionLimit]
  );

  return {
    ...search,
    selectedIds,
    orderedSelection: drag.orderedSelection,
    handleSelectionToggle,
    handleDragStart: drag.handleDragStart,
    handleDragOver: drag.handleDragOver,
    handleDrop: drag.handleDrop,
    handleDragEnd: drag.handleDragEnd,
    draggedId: drag.draggedId,
    setSelections,
  } as const;
};

import React from 'react';

export type ContainerSize = 'xs' | 'sm' | 'md' | 'lg' | 'xl';

export interface ContainerConfig<T> {
  xs?: T;
  sm?: T;
  md?: T;
  lg?: T;
  xl?: T;
}

export const containerClasses = {
  container: '@container',
  containerInline: '@container/inline',
  containerSize: '@container/size',
  xs: '@xs',
  sm: '@sm',
  md: '@md',
  lg: '@lg',
  xl: '@xl',
  '320px': '@[320px]',
  '384px': '@[384px]',
  '640px': '@[640px]',
  '768px': '@[768px]',
  '1024px': '@[1024px]',
  '1280px': '@[1280px]',
} as const;

export const getContainerValue = <T>(config: ContainerConfig<T>, fallback: T): T => {
  return fallback;
};

interface UseContainerResult {
  containerSize: ContainerSize;
  isXs: boolean;
  isSm: boolean;
  isMd: boolean;
  isLg: boolean;
  isXl: boolean;
  isSmUp: boolean;
  isMdUp: boolean;
  isLgUp: boolean;
}

export const useContainer = (containerRef?: React.RefObject<HTMLElement>): UseContainerResult => {
  const [containerSize, setContainerSize] = React.useState<ContainerSize>('sm');

  React.useEffect(() => {
    if (!containerRef?.current) return;

    const updateContainerSize = () => {
      if (!containerRef.current) return;

      const { width } = containerRef.current.getBoundingClientRect();

      if (width >= 1280) setContainerSize('xl');
      else if (width >= 1024) setContainerSize('lg');
      else if (width >= 768) setContainerSize('md');
      else if (width >= 384) setContainerSize('sm');
      else setContainerSize('xs');
    };

    updateContainerSize();

    const resizeObserver = new ResizeObserver(updateContainerSize);
    resizeObserver.observe(containerRef.current);

    return () => {
      resizeObserver.disconnect();
    };
  }, [containerRef]);

  return {
    containerSize,
    isXs: containerSize === 'xs',
    isSm: containerSize === 'sm',
    isMd: containerSize === 'md',
    isLg: containerSize === 'lg',
    isXl: containerSize === 'xl',
    isSmUp: ['sm', 'md', 'lg', 'xl'].includes(containerSize),
    isMdUp: ['md', 'lg', 'xl'].includes(containerSize),
    isLgUp: ['lg', 'xl'].includes(containerSize),
  };
};

export const getContainerClasses = (containerName?: string): string => {
  return containerName ? `@container/${containerName}` : '@container';
};

