import { Tafsir } from '../domain/entities/Tafsir';
import { ITafsirRepository } from '../domain/repositories/ITafsirRepository';
import { logger as Logger } from '../infrastructure/monitoring/Logger';

export async function getCachedResourcesWithFallback(repository: ITafsirRepository): Promise<{
  tafsirs: Tafsir[];
  isFromCache: boolean;
  error?: string;
}> {
  try {
    const cachedTafsirs = await repository.getCachedResources();

    if (cachedTafsirs.length > 0) {
      return {
        tafsirs: cachedTafsirs,
        isFromCache: true,
      };
    }

    return {
      tafsirs: [],
      isFromCache: false,
      error: 'No tafsir resources available. Please check your internet connection.',
    };
  } catch {
    return {
      tafsirs: [],
      isFromCache: false,
      error: 'Failed to load tafsir resources. Please try again.',
    };
  }
}

export async function getTafsirByIdWithCache(
  repository: ITafsirRepository,
  id: number
): Promise<Tafsir | null> {
  try {
    return await repository.getById(id);
  } catch (error) {
    Logger.warn('Failed to get tafsir by ID, trying cache:', undefined, error as Error);
    const cached = await repository.getCachedResources();
    return cached.find((t) => t.id === id) || null;
  }
}

import { getCachedResourcesWithFallback } from './getTafsirCache';
import { Tafsir } from '../domain/entities/Tafsir';
import { ITafsirRepository } from '../domain/repositories/ITafsirRepository';
import { logger as Logger } from '../infrastructure/monitoring/Logger';

export async function getTafsirsByLanguage(
  repository: ITafsirRepository,
  language: string
): Promise<{
  tafsirs: Tafsir[];
  isFromCache: boolean;
  error?: string;
}> {
  try {
    const tafsirs = await repository.getResourcesByLanguage(language);
    return { tafsirs, isFromCache: false };
  } catch (error) {
    Logger.warn(
      `Failed to fetch tafsir resources for language ${language}:`,
      undefined,
      error as Error
    );
    const cachedResult = await getCachedResourcesWithFallback(repository);
    return {
      tafsirs: cachedResult.tafsirs.filter((t) => t.isInLanguage(language)),
      isFromCache: true,
      error: cachedResult.error,
    };
  }
}

export async function searchTafsirs(
  repository: ITafsirRepository,
  searchTerm: string
): Promise<Tafsir[]> {
  if (!searchTerm.trim()) {
    try {
      const tafsirs = await repository.getAllResources();
      if (tafsirs.length > 0) return tafsirs;
      return (await getCachedResourcesWithFallback(repository)).tafsirs;
    } catch (error) {
      Logger.warn('Failed to fetch fresh tafsir resources:', undefined, error as Error);
      return (await getCachedResourcesWithFallback(repository)).tafsirs;
    }
  }

  try {
    return await repository.search(searchTerm);
  } catch (error) {
    Logger.warn('Search failed, using cached data:', undefined, error as Error);
    const cached = await repository.getCachedResources();
    return cached.filter((t) => t.matchesSearch(searchTerm));
  }
}

import {
  InvalidTafsirRequestError,
  TafsirContentLoadError,
} from '../../domain/errors/DomainErrors';
import { ITafsirRepository } from '../../domain/repositories/ITafsirRepository';
import { logger as Logger } from '../../infrastructure/monitoring/Logger';

/**
 * Use Case: Get Tafsir Content for a Verse
 *
 * Handles retrieving tafsir content for specific verses with caching and fallback.
 */
export class GetTafsirContentUseCase {
  constructor(private readonly tafsirRepository: ITafsirRepository) {}

  /**
   * Get tafsir content for a specific verse
   *
   * @param verseKey - Verse identifier (e.g., "1:1")
   * @param tafsirId - Tafsir resource ID
   * @returns Promise resolving to HTML content
   */
  async execute(verseKey: string, tafsirId: number): Promise<string> {
    if (!verseKey || !tafsirId) {
      throw new InvalidTafsirRequestError();
    }

    try {
      const content = await this.tafsirRepository.getTafsirByVerse(verseKey, tafsirId);

      if (!content || content.trim() === '') {
        return 'No tafsir content available for this verse.';
      }

      return content;
    } catch (error) {
      Logger.error('Failed to get tafsir content:', undefined, error as Error);
      throw new TafsirContentLoadError();
    }
  }

  /**
   * Get tafsir content for multiple tafsir sources
   *
   * @param verseKey - Verse identifier
   * @param tafsirIds - Array of tafsir resource IDs
   * @returns Promise resolving to map of tafsir ID to content
   */
  async executeMultiple(verseKey: string, tafsirIds: number[]): Promise<Map<number, string>> {
    const results = new Map<number, string>();

    const promises = tafsirIds.map(async (tafsirId) => {
      try {
        const content = await this.execute(verseKey, tafsirId);
        results.set(tafsirId, content);
      } catch (error) {
        Logger.warn(`Failed to get tafsir content for ID ${tafsirId}:`, undefined, error as Error);
        results.set(tafsirId, 'Failed to load tafsir content.');
      }
    });

    await Promise.allSettled(promises);
    return results;
  }
}

import { Tafsir } from '../../domain/entities/Tafsir';
import { ITafsirRepository } from '../../domain/repositories/ITafsirRepository';
import { logger as Logger } from '../../infrastructure/monitoring/Logger';
import { getCachedResourcesWithFallback, getTafsirByIdWithCache } from '../getTafsirCache';
import { getTafsirsByLanguage, searchTafsirs } from '../getTafsirFilters';

/**
 * Use Case: Get Tafsir Resources
 *
 * Orchestrates retrieval and management of tafsir resources.
 * Delegates caching and filtering to helper modules.
 */
export class GetTafsirResourcesUseCase {
  constructor(private readonly tafsirRepository: ITafsirRepository) {}

  async execute(): Promise<{
    tafsirs: Tafsir[];
    isFromCache: boolean;
    error?: string;
  }> {
    try {
      const tafsirs = await this.tafsirRepository.getAllResources();
      if (tafsirs.length > 0) {
        return { tafsirs, isFromCache: false };
      }
      return getCachedResourcesWithFallback(this.tafsirRepository);
    } catch (error) {
      Logger.warn('Failed to fetch fresh tafsir resources:', undefined, error as Error);
      return getCachedResourcesWithFallback(this.tafsirRepository);
    }
  }

  async executeByLanguage(language: string): Promise<{
    tafsirs: Tafsir[];
    isFromCache: boolean;
    error?: string;
  }> {
    return getTafsirsByLanguage(this.tafsirRepository, language);
  }

  async search(searchTerm: string): Promise<Tafsir[]> {
    return searchTafsirs(this.tafsirRepository, searchTerm);
  }

  async getById(id: number): Promise<Tafsir | null> {
    return getTafsirByIdWithCache(this.tafsirRepository, id);
  }

  async getTafsirContent(verseKey: string, tafsirId: number): Promise<string> {
    return this.tafsirRepository.getTafsirByVerse(verseKey, tafsirId);
  }
}

