/**
 * Bookmark domain entity representing a bookmarked verse
 */
export class Bookmark {
  constructor(
    public readonly id: string,
    public readonly userId: string,
    public readonly verseId: string,
    public readonly position: BookmarkPosition,
    public readonly createdAt: Date,
    public readonly notes?: string,
    public readonly tags: string[] = []
  ) {
    this.validateInputs();
  }

  private validateInputs(): void {
    if (!this.id || this.id.trim() === '') {
      throw new Error('Bookmark ID cannot be empty');
    }

    if (!this.userId || this.userId.trim() === '') {
      throw new Error('User ID cannot be empty');
    }

    if (!this.verseId || this.verseId.trim() === '') {
      throw new Error('Verse ID cannot be empty');
    }

    if (!this.createdAt) {
      throw new Error('Created date is required');
    }
  }

  /**
   * Checks if bookmark belongs to a specific user
   */
  belongsToUser(userId: string): boolean {
    return this.userId === userId;
  }

  /**
   * Gets display text for the bookmark
   */
  getDisplayText(): string {
    let text = this.position.getDisplayText();
    if (this.notes && this.notes.trim().length > 0) {
      text += ` - ${this.notes}`;
    }
    if (this.tags.length > 0) {
      text += ` [${this.tags.join(', ')}]`;
    }
    return text;
  }

  /**
   * Checks equality based on ID
   */
  equals(other: Bookmark): boolean {
    return this.id === other.id;
  }

  /**
   * Converts to plain object for serialization
   */
  toPlainObject() {
    const hasNotes = Boolean(this.notes && this.notes.trim().length > 0);
    const hasTags = this.tags.length > 0;
    return {
      id: this.id,
      userId: this.userId,
      verseId: this.verseId,
      position: this.position.toPlainObject(),
      createdAt: this.createdAt.toISOString(),
      notes: this.notes,
      tags: this.tags,
      hasNotes,
      hasTags,
      displayText: this.getDisplayText(),
    };
  }
}

/**
 * Revelation type enum
 */
export enum RevelationType {
  MAKKI = 'makki',
  MADANI = 'madani',
}

import {
  getEstimatedReadingTime,
  getJuzNumbers,
  getMemorizationDifficulty,
  isLongSurah,
  isMediumSurah,
  isMufassalSurah,
  isSevenLongSurah,
  isShortSurah,
} from './surahHelpers';

/**
 * Surah domain entity representing a chapter of the Quran
 */
export class Surah {
  constructor(
    public readonly id: number,
    public readonly name: string,
    public readonly arabicName: string,
    public readonly englishName: string,
    public readonly englishTranslation: string,
    public readonly numberOfAyahs: number,
    public readonly revelationType: RevelationType,
    public readonly revelationOrder?: number
  ) {
    this.validateInputs();
  }

  private validateInputs(): void {
    if (this.id < 1 || this.id > 114) {
      throw new Error('Invalid Surah ID: must be between 1 and 114');
    }

    if (!this.name || this.name.trim() === '') {
      throw new Error('Surah name cannot be empty');
    }

    if (!this.arabicName || this.arabicName.trim() === '') {
      throw new Error('Arabic name cannot be empty');
    }

    if (!this.englishName || this.englishName.trim() === '') {
      throw new Error('English name cannot be empty');
    }

    if (this.numberOfAyahs < 1) {
      throw new Error('Number of ayahs must be positive');
    }
  }

  /** Checks if this Surah was revealed in Makkah */
  isMakki(): boolean {
    return this.revelationType === RevelationType.MAKKI;
  }

  /** Checks if this Surah was revealed in Madinah */
  isMadani(): boolean {
    return this.revelationType === RevelationType.MADANI;
  }

  /** Checks if this Surah can be read in prayer */
  canBeReadInPrayer(): boolean {
    return this.id !== 9; // At-Tawbah has no Bismillah
  }

  /** Checks if this Surah starts with Bismillah */
  startWithBismillah(): boolean {
    return this.id !== 9; // At-Tawbah
  }

  /** Checks equality based on ID */
  equals(other: Surah): boolean {
    return this.id === other.id;
  }

  /**
   * Converts to plain object for serialization
   */
  toPlainObject() {
    return {
      id: this.id,
      name: this.name,
      arabicName: this.arabicName,
      englishName: this.englishName,
      englishTranslation: this.englishTranslation,
      numberOfAyahs: this.numberOfAyahs,
      revelationType: this.revelationType,
      revelationOrder: this.revelationOrder,
      isMakki: this.isMakki(),
      isMadani: this.isMadani(),
      canBeReadInPrayer: this.canBeReadInPrayer(),
      startWithBismillah: this.startWithBismillah(),
      memorizationDifficulty: getMemorizationDifficulty(this.numberOfAyahs),
      estimatedReadingTime: getEstimatedReadingTime(this.numberOfAyahs),
      isShortSurah: isShortSurah(this.numberOfAyahs),
      isMediumSurah: isMediumSurah(this.numberOfAyahs),
      isLongSurah: isLongSurah(this.numberOfAyahs),
      isSevenLongSurah: isSevenLongSurah(this.id),
      isMufassalSurah: isMufassalSurah(this.id),
      juzNumbers: getJuzNumbers(this.id),
    };
  }
}

import { getEstimatedReadingTime, getWordCount, isSajdahVerse } from './verseUtils';
import { Translation } from '../value-objects/Translation';

/**
 * Verse domain entity representing a single Quranic verse
 */
export class Verse {
  constructor(
    public readonly id: string,
    public readonly surahId: number,
    public readonly ayahNumber: number,
    public readonly arabicText: string,
    public readonly uthmaniText: string,
    public readonly translation?: Translation
  ) {
    this.validateInputs();
  }

  private validateInputs(): void {
    if (!this.id || this.id.trim() === '') {
      throw new Error('Verse ID cannot be empty');
    }

    if (this.surahId < 1 || this.surahId > 114) {
      throw new Error('Invalid Surah ID');
    }

    if (this.ayahNumber < 1) {
      throw new Error('Invalid Ayah number');
    }

    if (!this.arabicText || this.arabicText.trim() === '') {
      throw new Error('Arabic text cannot be empty');
    }

    if (!this.uthmaniText || this.uthmaniText.trim() === '') {
      throw new Error('Uthmani text cannot be empty');
    }
  }

  /**
   * Returns the verse key in format "surah:ayah"
   */
  get verseKey(): string {
    return `${this.surahId}:${this.ayahNumber}`;
  }

  /**
   * Checks if this is the first verse of a Surah
   */
  isFirstVerse(): boolean {
    return this.ayahNumber === 1;
  }

  /**
   * Checks equality based on ID
   */
  equals(other: Verse): boolean {
    return this.id === other.id;
  }

  /**
   * Creates a new verse instance with translation
   */
  withTranslation(translation: Translation): Verse {
    return new Verse(
      this.id,
      this.surahId,
      this.ayahNumber,
      this.arabicText,
      this.uthmaniText,
      translation
    );
  }

  /**
   * Converts to plain object for serialization
   */
  toPlainObject() {
    return {
      id: this.id,
      surahId: this.surahId,
      ayahNumber: this.ayahNumber,
      verseKey: this.verseKey,
      arabicText: this.arabicText,
      uthmaniText: this.uthmaniText,
      translation: this.translation?.toPlainObject(),
      wordCount: getWordCount(this.arabicText),
      estimatedReadingTime: getEstimatedReadingTime(this.arabicText),
      isFirstVerse: this.isFirstVerse(),
      isSajdahVerse: isSajdahVerse(this.surahId, this.ayahNumber),
    };
  }
}

