import { v4 as uuidv4 } from 'uuid';

import { Bookmark } from '../entities';
import { IBookmarkRepository } from '../repositories/IBookmarkRepository';
import { IVerseRepository } from '../repositories/IVerseRepository';
import { BookmarkPosition } from '../value-objects/BookmarkPosition';
import { StoredBookmark } from '../value-objects/StoredBookmark';

/**
 * Handles bookmark import and export operations
 */
export class BookmarkImportService {
  constructor(
    private bookmarkRepository: IBookmarkRepository,
    private verseRepository: IVerseRepository
  ) {}

  /**
   * Imports bookmarks from external data
   */
  async importBookmarks(
    userId: string,
    importData: Array<{ surahId: number; ayahNumber: number; notes?: string; tags?: string[] }>
  ): Promise<Bookmark[]> {
    const validBookmarks: Bookmark[] = [];

    for (const data of importData) {
      try {
        const verse = await this.verseRepository.findBySurahAndAyah(data.surahId, data.ayahNumber);
        if (!verse) {
          continue;
        }

        const position = new BookmarkPosition(data.surahId, data.ayahNumber, new Date());
        const exists = await this.bookmarkRepository.existsAtPosition(userId, position);
        if (exists) {
          continue;
        }

        const bookmark = new Bookmark(
          uuidv4(),
          userId,
          verse.id,
          position,
          new Date(),
          data.notes,
          data.tags || []
        );

        validBookmarks.push(bookmark);
      } catch {
        continue;
      }
    }

    if (validBookmarks.length > 0) {
      await this.bookmarkRepository.saveMany(validBookmarks);
    }

    return validBookmarks;
  }

  /**
   * Exports user bookmarks
   */
  async exportBookmarks(userId: string): Promise<StoredBookmark[]> {
    return this.bookmarkRepository.exportBookmarks(userId);
  }
}

import { Bookmark, withNotes, withTags, withAddedTag, withRemovedTag } from '../entities';
import { BookmarkNotFoundError, UnauthorizedBookmarkError } from '../errors/DomainErrors';
import { IBookmarkRepository } from '../repositories/IBookmarkRepository';

/**
 * Domain service for bookmark tag and note mutations
 */
export class BookmarkMutationService {
  constructor(private bookmarkRepository: IBookmarkRepository) {}

  /**
   * Updates bookmark notes
   */
  async updateBookmarkNotes(userId: string, bookmarkId: string, notes: string): Promise<Bookmark> {
    const bookmark = await this.bookmarkRepository.findById(bookmarkId);
    if (!bookmark) {
      throw new BookmarkNotFoundError(bookmarkId);
    }

    if (!bookmark.belongsToUser(userId)) {
      throw new UnauthorizedBookmarkError('Cannot update bookmark belonging to another user');
    }

    const updatedBookmark = withNotes(bookmark, notes);
    await this.bookmarkRepository.save(updatedBookmark);
    return updatedBookmark;
  }

  /**
   * Updates bookmark tags
   */
  async updateBookmarkTags(userId: string, bookmarkId: string, tags: string[]): Promise<Bookmark> {
    const bookmark = await this.bookmarkRepository.findById(bookmarkId);
    if (!bookmark) {
      throw new BookmarkNotFoundError(bookmarkId);
    }

    if (!bookmark.belongsToUser(userId)) {
      throw new UnauthorizedBookmarkError('Cannot update bookmark belonging to another user');
    }

    const updatedBookmark = withTags(bookmark, tags);
    await this.bookmarkRepository.save(updatedBookmark);
    return updatedBookmark;
  }

  /**
   * Adds a tag to a bookmark
   */
  async addTagToBookmark(userId: string, bookmarkId: string, tag: string): Promise<Bookmark> {
    const bookmark = await this.bookmarkRepository.findById(bookmarkId);
    if (!bookmark) {
      throw new BookmarkNotFoundError(bookmarkId);
    }

    if (!bookmark.belongsToUser(userId)) {
      throw new UnauthorizedBookmarkError('Cannot update bookmark belonging to another user');
    }

    const updatedBookmark = withAddedTag(bookmark, tag);
    await this.bookmarkRepository.save(updatedBookmark);
    return updatedBookmark;
  }

  /**
   * Removes a tag from a bookmark
   */
  async removeTagFromBookmark(userId: string, bookmarkId: string, tag: string): Promise<Bookmark> {
    const bookmark = await this.bookmarkRepository.findById(bookmarkId);
    if (!bookmark) {
      throw new BookmarkNotFoundError(bookmarkId);
    }

    if (!bookmark.belongsToUser(userId)) {
      throw new UnauthorizedBookmarkError('Cannot update bookmark belonging to another user');
    }

    const updatedBookmark = withRemovedTag(bookmark, tag);
    await this.bookmarkRepository.save(updatedBookmark);
    return updatedBookmark;
  }
}

import { v4 as uuidv4 } from 'uuid';

import { Bookmark, Verse } from '../entities';
import {
  BookmarkAlreadyExistsError,
  VerseNotFoundError,
  BookmarkNotFoundError,
  UnauthorizedBookmarkError,
} from '../errors/DomainErrors';
import { IBookmarkRepository } from '../repositories/IBookmarkRepository';
import { IVerseRepository } from '../repositories/IVerseRepository';
import { BookmarkPosition } from '../value-objects/BookmarkPosition';

/**
 * Domain service for bookmark operations
 */
export class BookmarkService {
  constructor(
    private bookmarkRepository: IBookmarkRepository,
    private verseRepository: IVerseRepository
  ) {}

  /**
   * Creates a bookmark for a verse
   */
  async bookmarkVerse(
    userId: string,
    surahId: number,
    ayahNumber: number,
    notes?: string,
    tags: string[] = []
  ): Promise<Bookmark> {
    // Verify verse exists
    const verse = await this.verseRepository.findBySurahAndAyah(surahId, ayahNumber);
    if (!verse) {
      throw new VerseNotFoundError(`${surahId}:${ayahNumber}`);
    }

    // Check if bookmark already exists
    const position = new BookmarkPosition(surahId, ayahNumber, new Date());
    const exists = await this.bookmarkRepository.existsAtPosition(userId, position);
    if (exists) {
      throw new BookmarkAlreadyExistsError(userId, surahId, ayahNumber);
    }

    // Create new bookmark
    const bookmark = new Bookmark(uuidv4(), userId, verse.id, position, new Date(), notes, tags);

    await this.bookmarkRepository.save(bookmark);
    return bookmark;
  }

  /**
   * Removes a bookmark
   */
  async removeBookmark(userId: string, bookmarkId: string): Promise<void> {
    const bookmark = await this.bookmarkRepository.findById(bookmarkId);
    if (!bookmark) {
      throw new BookmarkNotFoundError(bookmarkId);
    }

    if (!bookmark.belongsToUser(userId)) {
      throw new UnauthorizedBookmarkError('Cannot remove bookmark belonging to another user');
    }

    await this.bookmarkRepository.remove(bookmarkId);
  }

  /**
   * Checks if a verse is bookmarked by user
   */
  async isVerseBookmarked(userId: string, surahId: number, ayahNumber: number): Promise<boolean> {
    const position = new BookmarkPosition(surahId, ayahNumber, new Date());
    return await this.bookmarkRepository.existsAtPosition(userId, position);
  }

  /**
   * Gets bookmarks with their corresponding verses
   */
  async getBookmarksWithVerses(
    userId: string,
    limit?: number
  ): Promise<Array<{ bookmark: Bookmark; verse: Verse }>> {
    const bookmarks = await this.bookmarkRepository.findByUser(userId);
    const limitedBookmarks = limit ? bookmarks.slice(0, limit) : bookmarks;

    const result: Array<{ bookmark: Bookmark; verse: Verse }> = [];

    for (const bookmark of limitedBookmarks) {
      const verse = await this.verseRepository.findById(bookmark.verseId);
      if (!verse) {
        throw new VerseNotFoundError(bookmark.verseId);
      }
      result.push({ bookmark, verse });
    }

    return result;
  }

  /**
   * Organizes bookmarks by Surah
   */
  async organizeBookmarksBySurah(userId: string): Promise<Map<number, Bookmark[]>> {
    const bookmarks = await this.bookmarkRepository.findByUser(userId);
    const organized = new Map<number, Bookmark[]>();

    bookmarks.forEach((bookmark) => {
      const surahId = bookmark.position.surahId;
      if (!organized.has(surahId)) {
        organized.set(surahId, []);
      }
      organized.get(surahId)!.push(bookmark);
    });

    // Sort bookmarks within each Surah by ayah number
    organized.forEach((surahBookmarks) => {
      surahBookmarks.sort((a, b) => a.position.ayahNumber - b.position.ayahNumber);
    });

    return organized;
  }
}

import * as comparison from './bookmarkPosition/comparison';
import * as navigation from './bookmarkPosition/navigation';

export class BookmarkPosition {
  constructor(
    public readonly surahId: number,
    public readonly ayahNumber: number,
    public readonly timestamp: Date
  ) {
    this.validateInputs();
  }

  private validateInputs(): void {
    if (this.surahId < 1 || this.surahId > 114) {
      throw new Error('Invalid Surah ID: must be between 1 and 114');
    }
    if (this.ayahNumber < 1) {
      throw new Error('Ayah number must be positive');
    }
    if (!this.timestamp) {
      throw new Error('Timestamp is required');
    }
  }

  get verseKey(): string {
    return `${this.surahId}:${this.ayahNumber}`;
  }

  toString(): string {
    return this.verseKey;
  }

  isFirstVerse(): boolean {
    return this.ayahNumber === 1;
  }

  isInSurah(surahId: number): boolean {
    return this.surahId === surahId;
  }

  getNextVerse(maxAyahInSurah: number): BookmarkPosition | null {
    return navigation.getNextVerse<BookmarkPosition>(this, maxAyahInSurah, BookmarkPosition);
  }

  getPreviousVerse(): BookmarkPosition | null {
    return navigation.getPreviousVerse<BookmarkPosition>(this, BookmarkPosition);
  }

  compareTo(other: BookmarkPosition): number {
    return comparison.compareTo(this, other);
  }

  isBefore(other: BookmarkPosition): boolean {
    return comparison.isBefore(this, other);
  }

  isAfter(other: BookmarkPosition): boolean {
    return comparison.isAfter(this, other);
  }

  isInSameSurah(other: BookmarkPosition): boolean {
    return comparison.isInSameSurah(this, other);
  }

  getDistanceFrom(other: BookmarkPosition): number | null {
    return comparison.getDistanceFrom(this, other);
  }

  isWithinRange(other: BookmarkPosition, maxDistance: number): boolean {
    return comparison.isWithinRange(this, other, maxDistance);
  }

  getDisplayText(): string {
    return `Surah ${this.surahId}, Verse ${this.ayahNumber}`;
  }

  equals(other: BookmarkPosition): boolean {
    return this.surahId === other.surahId && this.ayahNumber === other.ayahNumber;
  }

  withNewTimestamp(): BookmarkPosition {
    return new BookmarkPosition(this.surahId, this.ayahNumber, new Date());
  }

  toPlainObject() {
    return {
      surahId: this.surahId,
      ayahNumber: this.ayahNumber,
      verseKey: this.verseKey,
      timestamp: this.timestamp.toISOString(),
      isFirstVerse: this.isFirstVerse(),
      displayText: this.getDisplayText(),
    };
  }

  static fromVerseKey(verseKey: string): BookmarkPosition {
    const parts = verseKey.split(':');
    if (parts.length !== 2) {
      throw new Error('Invalid verse key format. Expected "surah:ayah"');
    }
    const surahId = parseInt(parts[0], 10);
    const ayahNumber = parseInt(parts[1], 10);
    if (isNaN(surahId) || isNaN(ayahNumber) || parts[0].trim() === '' || parts[1].trim() === '') {
      throw new Error('Invalid verse key: surah and ayah must be numbers');
    }
    return new BookmarkPosition(surahId, ayahNumber, new Date());
  }
}

export interface StoredBookmarkPosition {
  surahId: number;
  ayahNumber: number;
  verseKey: string;
  timestamp: string;
  isFirstVerse: boolean;
  displayText: string;
}

export interface StoredBookmark {
  id: string;
  userId: string;
  verseId: string;
  position: StoredBookmarkPosition;
  createdAt: string;
  notes?: string | undefined;
  tags: string[];
}

export function isStoredBookmark(value: unknown): value is StoredBookmark {
  if (typeof value !== 'object' || value === null) return false;
  const b = value as StoredBookmark;
  return (
    typeof b.id === 'string' &&
    typeof b.userId === 'string' &&
    typeof b.verseId === 'string' &&
    typeof b.createdAt === 'string' &&
    typeof b.position === 'object' &&
    b.position !== null &&
    typeof b.position.surahId === 'number' &&
    typeof b.position.ayahNumber === 'number' &&
    typeof b.position.timestamp === 'string'
  );
}

/**
 * Translation value object for verse translations
 */
export class Translation {
  constructor(
    public readonly id: number,
    public readonly resourceId: number,
    public readonly text: string,
    public readonly languageCode: string = 'en'
  ) {
    this.validateInputs();
  }

  private validateInputs(): void {
    if (this.id < 0) {
      throw new Error('Translation ID must be non-negative');
    }

    if (this.resourceId < 0) {
      throw new Error('Resource ID must be non-negative');
    }

    if (!this.text || this.text.trim() === '') {
      throw new Error('Translation text cannot be empty');
    }

    if (!this.languageCode || this.languageCode.trim() === '') {
      throw new Error('Language code cannot be empty');
    }
  }

  /**
   * Gets the word count of the translation text
   */
  getWordCount(): number {
    return this.text.split(/\s+/).filter((word) => word.trim().length > 0).length;
  }

  /**
   * Gets the character count including spaces
   */
  getCharacterCount(): number {
    return this.text.length;
  }

  /**
   * Checks if this is an English translation
   */
  isEnglish(): boolean {
    return this.languageCode.toLowerCase().startsWith('en');
  }

  /**
   * Checks if the translation contains a specific text (case-insensitive)
   */
  contains(searchText: string): boolean {
    if (!searchText) return true;
    return this.text.toLowerCase().includes(searchText.toLowerCase());
  }

  /**
   * Gets a preview of the translation with word limit
   */
  getPreview(wordLimit: number = 10): string {
    const words = this.text.split(/\s+/);
    if (words.length <= wordLimit) {
      return this.text;
    }
    return words.slice(0, wordLimit).join(' ') + '...';
  }

  /**
   * Checks if this is a long translation (more than 50 words)
   */
  isLong(): boolean {
    return this.getWordCount() > 50;
  }

  /**
   * Checks equality based on ID and resource ID
   */
  equals(other: Translation): boolean {
    return this.id === other.id && this.resourceId === other.resourceId;
  }

  /**
   * Converts to plain object for serialization
   */
  toPlainObject() {
    return {
      id: this.id,
      resourceId: this.resourceId,
      text: this.text,
      languageCode: this.languageCode,
      wordCount: this.getWordCount(),
      characterCount: this.getCharacterCount(),
      isEnglish: this.isEnglish(),
      isLong: this.isLong(),
      preview: this.getPreview(),
    };
  }
}

import { IBookmarkRepository } from '../../domain/repositories/IBookmarkRepository';
import { ITafsirRepository } from '../../domain/repositories/ITafsirRepository';
import { IVerseRepository } from '../../domain/repositories/IVerseRepository';
import { BookmarkRepository } from '../repositories/BookmarkRepository';
import { TafsirRepository } from '../repositories/TafsirRepository';
import { VerseRepository } from '../repositories/VerseRepository';

/**
 * Dependency Injection Container
 * Centralizes creation and management of repository instances
 */
class DIContainer {
  private static instance: DIContainer;
  private verseRepository?: IVerseRepository;
  private bookmarkRepository?: IBookmarkRepository;
  private tafsirRepository?: ITafsirRepository;

  private constructor() {}

  static getInstance(): DIContainer {
    if (!DIContainer.instance) {
      DIContainer.instance = new DIContainer();
    }
    return DIContainer.instance;
  }

  /**
   * Get verse repository instance (singleton)
   */
  getVerseRepository(): IVerseRepository {
    if (!this.verseRepository) {
      this.verseRepository = new VerseRepository();
    }
    return this.verseRepository;
  }

  /**
   * Get bookmark repository instance (singleton)
   */
  getBookmarkRepository(): IBookmarkRepository {
    if (!this.bookmarkRepository) {
      this.bookmarkRepository = new BookmarkRepository();
    }
    return this.bookmarkRepository;
  }

  /**
   * Get tafsir repository instance (singleton)
   */
  getTafsirRepository(): ITafsirRepository {
    if (!this.tafsirRepository) {
      this.tafsirRepository = new TafsirRepository();
    }
    return this.tafsirRepository;
  }

  /**
   * Reset all instances (useful for testing)
   */
  reset(): void {
    this.verseRepository = undefined;
    this.bookmarkRepository = undefined;
    this.tafsirRepository = undefined;
  }

  /**
   * Set custom repository implementations (useful for testing)
   */
  setVerseRepository(repository: IVerseRepository): void {
    this.verseRepository = repository;
  }

  setBookmarkRepository(repository: IBookmarkRepository): void {
    this.bookmarkRepository = repository;
  }

  setTafsirRepository(repository: ITafsirRepository): void {
    this.tafsirRepository = repository;
  }
}

export const container = DIContainer.getInstance();

