import { inject, injectable, named } from 'inversify';

import { TYPES, type TypeKeys } from '../types';

/**
 * Injectable decorator with automatic registration
 *
 * Marks a class as injectable and available for dependency injection.
 * Use this on all classes that will be injected as dependencies.
 */
export const Injectable = (): ClassDecorator => injectable();

/**
 * Type-safe inject decorator
 *
 * Injects a dependency using type-safe symbols from TYPES.
 *
 * @param typeKey - Key from TYPES object
 * @returns Decorator function
 */
export const Inject = (typeKey: TypeKeys): ParameterDecorator & PropertyDecorator =>
  inject(TYPES[typeKey]);

/**
 * Named injection decorator
 *
 * Injects a named dependency when multiple implementations exist.
 *
 * @param typeKey - Key from TYPES object
 * @param name - Named identifier for the specific implementation
 * @returns Decorator function
 */
export const InjectNamed = (
  typeKey: TypeKeys,
  name: string
): ParameterDecorator & PropertyDecorator => {
  const injectDecorator = inject(TYPES[typeKey]);
  const namedDecorator = named(name);
  return ((target: object, key: string | symbol, index?: number) => {
    if (typeof index === 'number') {
      (injectDecorator as ParameterDecorator)(target, key, index);
      (namedDecorator as ParameterDecorator)(target, key, index);
    } else {
      (injectDecorator as PropertyDecorator)(target, key);
      (namedDecorator as PropertyDecorator)(target, key);
    }
  }) as unknown as ParameterDecorator & PropertyDecorator;
};

export { named } from 'inversify';

/**
 * Base Application Error
 *
 * All custom application errors should extend from this base class.
 * Provides consistent error structure and metadata.
 */
export class ApplicationError extends Error {
  public readonly timestamp: Date;
  public readonly stackTrace?: string;

  constructor(
    message: string,
    public readonly code: string,
    public readonly statusCode: number = 500,
    public readonly isOperational: boolean = true,
    public readonly context?: Record<string, unknown>,
    public readonly cause?: Error
  ) {
    super(message);

    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, this.constructor);
    }

    this.name = this.constructor.name;
    this.timestamp = new Date();
    this.stackTrace = this.stack;
  }

  toJSON(): Record<string, unknown> {
    return {
      name: this.name,
      message: this.message,
      code: this.code,
      statusCode: this.statusCode,
      isOperational: this.isOperational,
      timestamp: this.timestamp.toISOString(),
      context: this.context,
      cause: this.cause?.message,
      stack: this.stackTrace,
    };
  }

  getUserMessage(): string {
    return this.message;
  }
}

export default ApplicationError;

/**
 * Centralized Error Handler
 *
 * Provides consistent error handling across the application.
 * Handles logging, user notifications, and error recovery strategies.
 */

import {
  errorHandlerConfig,
  type ErrorHandlerOptions,
  type ErrorLogger,
  type ErrorReporter,
  type ErrorNotifier,
} from './ErrorHandlerConfig';
import { notifyError, setRetryCallback } from './errorNotifications';
import { ErrorFactory } from './factory';
import { isApplicationError } from './guards';
import { logger } from '../monitoring/Logger';

export class ErrorHandler {
  /**
   * Configure global error handler
   */
  static configure({
    logger: log,
    reporter,
    notifier,
    defaultOptions,
    retryCallback,
  }: {
    logger?: ErrorLogger;
    reporter?: ErrorReporter;
    notifier?: ErrorNotifier;
    defaultOptions?: Partial<ErrorHandlerOptions>;
    retryCallback?: () => void;
  }): void {
    if (log) errorHandlerConfig.setLogger(log);
    if (reporter) errorHandlerConfig.setReporter(reporter);
    if (notifier) errorHandlerConfig.setNotifier(notifier);
    if (defaultOptions) errorHandlerConfig.setDefaultOptions(defaultOptions);
    if (retryCallback) setRetryCallback(retryCallback);
  }

  /**
   * Handle any error with full error processing pipeline
   */
  static async handle(error: unknown, options: ErrorHandlerOptions = {}): Promise<unknown> {
    const appError = isApplicationError(error)
      ? error
      : ErrorFactory.fromUnknownError(error, options.context);

    const finalOptions = {
      ...errorHandlerConfig.getDefaultOptions(),
      ...options,
    };

    if (finalOptions.logError) {
      const configuredLogger = errorHandlerConfig.getLogger();
      if (configuredLogger) {
        configuredLogger(appError, options.context);
      } else {
        logger.error('[ErrorHandler]', options.context, appError);
      }
    }

    if (finalOptions.reportError && appError.isOperational) {
      const reporter = errorHandlerConfig.getReporter();
      if (reporter) {
        try {
          reporter(appError, options.context);
        } catch (reportError) {
          logger.error('[ErrorHandler] Failed to report error', undefined, reportError as Error);
        }
      }
    }

    if (finalOptions.showUserNotification) {
      notifyError(appError);
    }

    if (finalOptions.rethrow) {
      throw appError;
    }

    return options.fallback;
  }

  /**
   * Handle error without throwing (safe wrapper)
   */
  static async handleSafe<T>(
    operation: () => Promise<T>,
    options: ErrorHandlerOptions & { fallback: T }
  ): Promise<T> {
    try {
      return await operation();
    } catch (error) {
      return (await ErrorHandler.handle(error, { ...options, rethrow: false })) as T;
    }
  }

  /**
   * Handle error synchronously
   */
  static handleSync(error: unknown, options: ErrorHandlerOptions = {}): unknown {
    const appError = isApplicationError(error)
      ? error
      : ErrorFactory.fromUnknownError(error, options.context);

    const finalOptions = {
      ...errorHandlerConfig.getDefaultOptions(),
      ...options,
    };

    if (finalOptions.logError) {
      const configuredLogger = errorHandlerConfig.getLogger();
      if (configuredLogger) {
        configuredLogger(appError, options.context);
      } else {
        logger.error('[ErrorHandler]', options.context, appError);
      }
    }

    if (finalOptions.showUserNotification) {
      notifyError(appError);
    }

    if (finalOptions.reportError && appError.isOperational) {
      const reporter = errorHandlerConfig.getReporter();
      if (reporter) {
        Promise.resolve().then(() => {
          try {
            reporter(appError, options.context);
          } catch (reportError) {
            logger.error('[ErrorHandler] Failed to report error', undefined, reportError as Error);
          }
        });
      }
    }

    if (finalOptions.rethrow) {
      throw appError;
    }

    return options.fallback;
  }
}

import { ApplicationError } from './ApplicationError';
import { errorHandlerConfig } from './ErrorHandlerConfig';
import { logger } from '../monitoring/Logger';

/**
 * Error notification interface
 */
export interface ErrorNotification {
  title: string;
  message: string;
  type: 'error' | 'warning' | 'info';
  duration?: number;
  actions?: Array<{
    label: string;
    action: () => void;
  }>;
}

let retryCallback: () => void = () => {};

export function setRetryCallback(callback: () => void): void {
  retryCallback = callback;
}

export function getRetryCallback(): () => void {
  return retryCallback;
}

/**
 * Create user notification from error
 */
export function createNotification(error: ApplicationError): ErrorNotification {
  const baseNotification: ErrorNotification = {
    title: 'Error',
    message: error.getUserMessage(),
    type: 'error',
    duration: 5000,
  };

  switch (error.code) {
    case 'NETWORK_ERROR':
      return {
        ...baseNotification,
        title: 'Connection Error',
        actions: [
          {
            label: 'Retry',
            action: () => getRetryCallback()(),
          },
        ],
      };
    case 'VALIDATION_ERROR':
      return {
        ...baseNotification,
        title: 'Validation Error',
        type: 'warning',
        duration: 3000,
      };
    case 'AUTHENTICATION_ERROR':
      return {
        ...baseNotification,
        title: 'Authentication Required',
        actions: [
          {
            label: 'Sign In',
            action: () => {
              logger.info('Redirect to sign in');
            },
          },
        ],
      };
    case 'NOT_FOUND':
      return {
        ...baseNotification,
        title: 'Not Found',
        type: 'info',
        duration: 3000,
      };
    case 'RATE_LIMIT_ERROR':
      return {
        ...baseNotification,
        title: 'Rate Limit Exceeded',
        type: 'warning',
        duration: 8000,
      };
    case 'AUDIO_ERROR':
      return {
        ...baseNotification,
        title: 'Audio Error',
        actions: [
          {
            label: 'Try Again',
            action: () => {
              logger.info('Retry audio playback');
            },
          },
        ],
      };
    default:
      return baseNotification;
  }
}

/**
 * Notify user about an error using configured notifier
 */
export function notifyError(error: ApplicationError): void {
  const notifier = errorHandlerConfig.getNotifier();
  if (!notifier) return;

  const notification = createNotification(error);

  try {
    notifier(notification);
  } catch (notifyError) {
    logger.error('[ErrorHandler] Failed to show notification', undefined, notifyError as Error);
  }
}

import { ApplicationError } from './ApplicationError';
