import { RemoteTransport } from './RemoteTransport';
import { LogLevel } from './types';
import { config } from '../../../config';

import type { Logger } from './Logger';

export function parseLogLevel(level: string): LogLevel {
  const levelMap: Record<string, LogLevel> = {
    debug: LogLevel.DEBUG,
    info: LogLevel.INFO,
    warn: LogLevel.WARN,
    error: LogLevel.ERROR,
  };
  return levelMap[level.toLowerCase()] ?? LogLevel.INFO;
}

export function setupDefaultTransports(logger: Logger): void {
  if (!config.logging.enableRemote) return;
  // Add remote transport if endpoint is configured
  const endpoint = process.env.LOG_ENDPOINT;
  const apiKey = process.env.LOG_API_KEY;
  if (endpoint) {
    logger.addTransport(new RemoteTransport(endpoint, { apiKey }));
  }
}

export function getSource(): string | undefined {
  const inBrowser = typeof window !== 'undefined' && typeof window.Error !== 'undefined';
  if (!inBrowser) return undefined;
  try {
    const stack = new Error().stack;
    if (!stack) return undefined;
    const lines = stack.split('\n');
    // Find the first line that's not from this logger
    for (let i = 3; i < lines.length; i++) {
      const line = lines[i];
      const isFromLogger = line?.includes('Logger.ts') || line?.includes('console.');
      if (line && !isFromLogger) return line.trim();
    }
  } catch {
    // Ignore errors in source detection
  }
  return undefined;
}

import { LogLevel, type LogEntry, type ILoggerTransport } from './types';
import { fetchWithTimeout } from '../../../lib/api/client';

/**
 * Remote transport for sending logs to external services
 */
export class RemoteTransport implements ILoggerTransport {
  private buffer: LogEntry[] = [];
  private flushInterval: number;
  private endpoint: string;
  private apiKey?: string;
  private batchSize: number;
  private flushTimer?: NodeJS.Timeout;

  constructor(
    endpoint: string,
    options: {
      apiKey?: string;
      flushInterval?: number;
      batchSize?: number;
    } = {}
  ) {
    this.endpoint = endpoint;
    this.apiKey = options.apiKey;
    this.flushInterval = options.flushInterval || 10000; // 10 seconds
    this.batchSize = options.batchSize || 100;

    // Auto-flush on interval
    if (typeof window === 'undefined') {
      // Node.js environment
      this.flushTimer = setInterval(() => {
        this.flush();
      }, this.flushInterval);
    }
  }

  log(entry: LogEntry): void {
    this.buffer.push(entry);

    // Flush immediately for errors, or when buffer is full
    if (entry.level >= LogLevel.ERROR || this.buffer.length >= this.batchSize) {
      this.flush();
    }
  }

  async flush(): Promise<void> {
    if (this.buffer.length === 0) return;

    const entries = [...this.buffer];
    this.buffer = [];

    try {
      await fetchWithTimeout(this.endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(this.apiKey ? { Authorization: `Bearer ${this.apiKey}` } : {}),
        },
        body: JSON.stringify({ entries }),
        errorPrefix: 'Failed to send logs',
      });
    } catch {
      // Re-add failed entries to buffer for retry
      this.buffer.unshift(...entries);
      // Unable to log the failure via central logger to avoid cycles
    }
  }

  destroy(): void {
    if (this.flushTimer) {
      clearInterval(this.flushTimer);
    }
    this.flush();
  }
}

