export type { IErrorTracker, ErrorContext } from './types';

import { fetchWithTimeout } from '../../../../lib/api/client';
import { isApplicationError } from '../../errors';
import { logger } from '../Logger';

import type { ErrorContext, IErrorTracker } from './types';
import type { ApplicationError } from '../../errors';

export class RemoteErrorTracker implements IErrorTracker {
  private endpoint: string;
  private apiKey?: string;
  private buffer: Array<{
    type: 'error' | 'message';
    data: unknown;
    timestamp: number;
  }> = [];
  private flushTimer?: NodeJS.Timeout;

  constructor(endpoint: string, apiKey?: string) {
    this.endpoint = endpoint;
    this.apiKey = apiKey;

    // Auto-flush every 30 seconds
    if (typeof window === 'undefined') {
      this.flushTimer = setInterval(() => {
        this.flush();
      }, 30000);
    }
  }

  captureError(error: Error | ApplicationError, context?: ErrorContext): void {
    const errorData = {
      message: error.message,
      stack: error.stack,
      name: error.name,
      ...(isApplicationError(error)
        ? {
            code: error.code,
            statusCode: error.statusCode,
            isOperational: error.isOperational,
            context: error.context,
          }
        : {}),
      trackingContext: context,
    };

    this.buffer.push({
      type: 'error',
      data: errorData,
      timestamp: Date.now(),
    });

    // Flush immediately for fatal errors
    if (context?.level === 'fatal') {
      void this.flush();
    }
  }

  captureMessage(message: string, level = 'info', context?: ErrorContext): void {
    this.buffer.push({
      type: 'message',
      data: {
        message,
        level,
        context,
      },
      timestamp: Date.now(),
    });
  }

  setUser(user: { id?: string; email?: string; username?: string }): void {
    // Store user context for future events
    this.buffer.push({
      type: 'message',
      data: {
        message: 'User context set',
        level: 'info',
        context: { user },
      },
      timestamp: Date.now(),
    });
  }

  setContext(key: string, data: Record<string, unknown>): void {
    // Store context for future events
    this.buffer.push({
      type: 'message',
      data: {
        message: `Context set: ${key}`,
        level: 'info',
        context: { [key]: data },
      },
      timestamp: Date.now(),
    });
  }

  addBreadcrumb(breadcrumb: {
    message: string;
    category?: string;
    level?: string;
    timestamp?: number;
    data?: Record<string, unknown>;
  }): void {
    this.buffer.push({
      type: 'message',
      data: {
        message: `Breadcrumb: ${breadcrumb.message}`,
        level: 'info',
        context: { breadcrumb },
      },
      timestamp: Date.now(),
    });
  }

  async flush(): Promise<void> {
    if (this.buffer.length === 0) return;

    const events = [...this.buffer];
    this.buffer = [];

    try {
      await fetchWithTimeout(this.endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(this.apiKey ? { Authorization: `Bearer ${this.apiKey}` } : {}),
        },
        body: JSON.stringify({ events }),
        errorPrefix: 'Failed to send error tracking data',
      });
    } catch (error) {
      logger.warn('Failed to send error tracking data', undefined, error as Error);
      // Re-add events to buffer for retry
      this.buffer.unshift(...events);
    }
  }

  destroy(): void {
    if (this.flushTimer) {
      clearInterval(this.flushTimer);
    }
    void this.flush();
  }
}

import { logger } from '../Logger';
import { ConsoleErrorTracker } from './ConsoleErrorTracker';

import type { ErrorContext, IErrorTracker } from './types';
import type { ApplicationError } from '../../errors';

interface SentryLike {
  withScope: (callback: (scope: SentryScope) => void) => void;
  captureException: (error: Error) => void;
  captureMessage: (message: string, level?: string) => void;
  setUser: (user: { id?: string; email?: string; username?: string }) => void;
  setContext: (key: string, data: Record<string, unknown>) => void;
  addBreadcrumb: (breadcrumb: Record<string, unknown>) => void;
  flush: (timeout?: number) => Promise<boolean>;
}

interface SentryScope {
  setUser: (user: { id?: string }) => void;
  setLevel: (level: string) => void;
  setTag: (key: string, value: string) => void;
  setFingerprint: (fingerprint: string[]) => void;
  setContext: (key: string, data: Record<string, unknown>) => void;
}

export class SentryErrorTracker implements IErrorTracker {
  private isEnabled = false;
  private sentry: SentryLike | null = null;

  constructor() {
    this.initialize();
  }

  private async initialize(): Promise<void> {
    try {
      // Dynamically import Sentry if available
      this.sentry = (await import('@sentry/nextjs')) as SentryLike;
      this.isEnabled = true;

      logger.info('Sentry error tracker initialized');
    } catch {
      logger.warn('Sentry not available, falling back to console error tracking');
      this.isEnabled = false;
    }
  }

  captureError(error: Error | ApplicationError, context?: ErrorContext): void {
    if (!this.isEnabled || !this.sentry) {
      // Fallback to console
      new ConsoleErrorTracker().captureError(error, context);
      return;
    }

    this.sentry.withScope((scope: SentryScope) => {
      if (context) {
        if (context.userId) scope.setUser({ id: context.userId });
        if (context.level) scope.setLevel(context.level);
        if (context.tags) {
          Object.entries(context.tags).forEach(([key, value]) => {
            scope.setTag(key, value);
          });
        }
        if (context.fingerprint) scope.setFingerprint(context.fingerprint);
        if (context.component) scope.setTag('component', context.component);
        if (context.action) scope.setTag('action', context.action);
        if (context.metadata) {
          scope.setContext('metadata', context.metadata);
        }
      }

      this.sentry!.captureException(error);
    });
  }

  captureMessage(message: string, level = 'info', context?: ErrorContext): void {
    if (!this.isEnabled || !this.sentry) {
      // Fallback to console
      new ConsoleErrorTracker().captureMessage(message, level, context);
      return;
    }

    this.sentry.withScope((scope: SentryScope) => {
      if (context) {
        if (context.userId) scope.setUser({ id: context.userId });
        if (context.tags) {
          Object.entries(context.tags).forEach(([key, value]) => {
            scope.setTag(key, value);
          });
        }
        if (context.metadata) {
          scope.setContext('metadata', context.metadata);
        }
      }

      this.sentry!.captureMessage(message, level);
    });
  }

  setUser(user: { id?: string; email?: string; username?: string }): void {
    if (!this.isEnabled || !this.sentry) return;

    this.sentry.setUser(user);
  }

  setContext(key: string, data: Record<string, unknown>): void {
    if (!this.isEnabled || !this.sentry) return;

    this.sentry.setContext(key, data);
  }

  addBreadcrumb(breadcrumb: {
    message: string;
    category?: string;
    level?: string;
    timestamp?: number;
    data?: Record<string, unknown>;
  }): void {
    if (!this.isEnabled || !this.sentry) return;

    this.sentry.addBreadcrumb({
      message: breadcrumb.message,
      category: breadcrumb.category || 'default',
      level: breadcrumb.level || 'info',
      timestamp: breadcrumb.timestamp || Date.now() / 1000,
      data: breadcrumb.data,
    });
  }

  async flush(): Promise<void> {
    if (!this.isEnabled || !this.sentry) return;

    try {
      await this.sentry.flush(5000); // 5 second timeout
    } catch (error) {
      logger.warn('Failed to flush Sentry errors:', error);
    }
  }
}

import type { ApplicationError } from '../../errors';

export interface ErrorContext {
  userId?: string;
  sessionId?: string;
  requestId?: string;
  userAgent?: string;
  url?: string;
  component?: string;
  action?: string;
  metadata?: Record<string, unknown>;
  tags?: Record<string, string>;
  fingerprint?: string[];
  level?: 'fatal' | 'error' | 'warning' | 'info';
}

export interface IErrorTracker {
  captureError(error: Error | ApplicationError, context?: ErrorContext): void;
  captureMessage(message: string, level?: string, context?: ErrorContext): void;
  setUser(user: { id?: string; email?: string; username?: string }): void;
  setContext(key: string, data: Record<string, unknown>): void;
  addBreadcrumb(breadcrumb: {
    message: string;
    category?: string;
    level?: string;
    timestamp?: number;
    data?: Record<string, unknown>;
  }): void;
  flush(): Promise<void>;
}

import type { ErrorContext, IErrorTracker } from './types';
import type { ApplicationError } from '../../errors';

export type Breadcrumb = Parameters<IErrorTracker['addBreadcrumb']>[0];

let breadcrumbs: Breadcrumb[] = [];
const MAX_BREADCRUMBS = 100;

export function addBreadcrumb(tracker: IErrorTracker, breadcrumb: Breadcrumb): void {
  const enhancedBreadcrumb = {
    timestamp: Date.now() / 1000,
    ...breadcrumb,
  };

  breadcrumbs.push(enhancedBreadcrumb);

  if (breadcrumbs.length > MAX_BREADCRUMBS) {
    breadcrumbs = breadcrumbs.slice(-MAX_BREADCRUMBS);
  }

  tracker.addBreadcrumb(enhancedBreadcrumb);
}

export function getBreadcrumbs(limit = 10): Breadcrumb[] {
  return breadcrumbs.slice(-limit);
}

export function setupGlobalHandlers(
  captureError: (error: Error | ApplicationError, context?: ErrorContext) => void
): void {
  if (typeof window === 'undefined') return;

  window.addEventListener('error', (event) => {
    captureError(event.error || new Error(event.message), {
      component: 'global',
      action: 'unhandled_error',
      metadata: {
        filename: event.filename,
        lineno: event.lineno,
        colno: event.colno,
      },
    });
  });

  window.addEventListener('unhandledrejection', (event) => {
    captureError(event.reason, {
      component: 'global',
      action: 'unhandled_rejection',
    });
  });
}

import { toDomain, toPersistence } from './bookmark/bookmarkMapper';
import { isStoredBookmarkArray, dedupe } from './bookmark/bookmarkValidation';
import * as queries from './bookmark/queries';
import { Bookmark, hasNotes } from '../../domain/entities';
import { IBookmarkRepository } from '../../domain/repositories/IBookmarkRepository';
import { StoredBookmark } from '../../domain/value-objects/StoredBookmark';
import { logger } from '../monitoring/Logger';
import { getStoredBookmarks, saveStoredBookmarks } from './bookmark/storage';

export class BookmarkRepository implements IBookmarkRepository {
  async findById(id: string): Promise<Bookmark | null> {
    const stored = getStoredBookmarks();
    const bookmark = stored.find((b) => b.id === id);
    return bookmark ? toDomain(bookmark) : null;
  }

  async save(bookmark: Bookmark): Promise<void> {
    const stored = getStoredBookmarks();
    const index = stored.findIndex((b) => b.id === bookmark.id);
    const bookmarkData = toPersistence(bookmark);
    if (index >= 0) stored[index] = bookmarkData;
    else stored.push(bookmarkData);
    saveStoredBookmarks(stored);
  }

  async remove(id: string): Promise<void> {
    saveStoredBookmarks(getStoredBookmarks().filter((b) => b.id !== id));
  }

  async exists(id: string): Promise<boolean> {
    return getStoredBookmarks().some((b) => b.id === id);
  }

  existsByUserAndVerse = queries.existsByUserAndVerse;
  findByUser = queries.findByUser;
  findByUserWithOptions = queries.findByUserWithOptions;
  findRecent = queries.findRecent;
  findByVerse = queries.findByVerse;
  findBySurah = queries.findBySurah;
  findBySurahRange = queries.findBySurahRange;
  findByPosition = queries.findByPosition;
  existsAtPosition = queries.existsAtPosition;
  findByTags = queries.findByTags;
  getTagsByUser = queries.getTagsByUser;
  findWithNotes = queries.findWithNotes;
  findByDateRange = queries.findByDateRange;
  search = queries.search;
  findNext = queries.findNext;
  findPrevious = queries.findPrevious;
  findNearPosition = queries.findNearPosition;

  async saveMany(bookmarks: Bookmark[]): Promise<void> {
    for (const bookmark of bookmarks) await this.save(bookmark);
  }

  async removeMany(ids: string[]): Promise<void> {
    for (const id of ids) await this.remove(id);
  }

  async removeAllByUser(userId: string): Promise<void> {
    saveStoredBookmarks(getStoredBookmarks().filter((b) => b.userId !== userId));
  }

  async removeBySurah(userId: string, surahId: number): Promise<void> {
    saveStoredBookmarks(
      getStoredBookmarks().filter((b) => !(b.userId === userId && b.position.surahId === surahId))
    );
  }

  async getCountByUser(userId: string): Promise<number> {
    return (await queries.findByUser(userId)).length;
  }

  async getCountBySurah(userId: string, surahId: number): Promise<number> {
    return (await queries.findBySurah(surahId)).filter((b) => b.belongsToUser(userId)).length;
  }

  async getStatistics(userId: string): Promise<{
    totalBookmarks: number;
    surahsCovered: number;
    mostBookmarkedSurah: { surahId: number; count: number } | null;
    tagsUsed: number;
    bookmarksWithNotes: number;
  }> {
    const userBookmarks = await queries.findByUser(userId);
    const surahCounts: Record<number, number> = {};
    userBookmarks.forEach((b) => {
      surahCounts[b.position.surahId] = (surahCounts[b.position.surahId] || 0) + 1;
    });
    let mostBookmarkedSurah: { surahId: number; count: number } | null = null;
    for (const [surahId, count] of Object.entries(surahCounts)) {
      if (!mostBookmarkedSurah || count > mostBookmarkedSurah.count) {
        mostBookmarkedSurah = { surahId: parseInt(surahId), count };
      }
    }
    const allTags = userBookmarks.flatMap((b) => b.tags);
    const uniqueTags = new Set(allTags);
    return {
      totalBookmarks: userBookmarks.length,
      surahsCovered: Object.keys(surahCounts).length,
      mostBookmarkedSurah,
      tagsUsed: uniqueTags.size,
      bookmarksWithNotes: userBookmarks.filter((b) => hasNotes(b)).length,
    };
  }

  async exportBookmarks(userId: string): Promise<StoredBookmark[]> {
    return (await queries.findByUser(userId)).map((b) => toPersistence(b));
  }

  async importBookmarks(userId: string, bookmarks: StoredBookmark[]): Promise<void> {
    if (!isStoredBookmarkArray(bookmarks)) {
      logger.warn('Invalid bookmark data for import');
      return;
    }
    for (const b of dedupe(bookmarks)) {
      const bookmark = toDomain({ ...b, id: b.id || crypto.randomUUID(), userId });
      await this.save(bookmark);
    }
  }

  async cacheForOffline(userId: string): Promise<void> {
    logger.info('Bookmarks already cached offline', { userId });
  }

  async clearCache(userId: string): Promise<void> {
    await this.removeAllByUser(userId);
  }
}

import { fetchResourcesForLanguage } from './tafsirApi';
import {
  cacheResources as cacheTafsirResources,
  getCachedResources as getTafsirCachedResources,
} from './tafsirCache';
import { apiFetch } from '../../../lib/api/client';
import { Tafsir } from '../../domain/entities/Tafsir';
import { ITafsirRepository } from '../../domain/repositories/ITafsirRepository';
import { logger } from '../monitoring/Logger';

/**
 * Infrastructure implementation of ITafsirRepository
 *
 * Handles external API communication and caching for Tafsir resources.
 * Uses the existing lib/api infrastructure but follows clean architecture patterns.
 */
export class TafsirRepository implements ITafsirRepository {
  /**
   * Get all available tafsir resources across languages
   */
  async getAllResources(): Promise<Tafsir[]> {
    try {
      // Try getting comprehensive list first
      const allResources = await fetchResourcesForLanguage('all');
      if (allResources.length > 1) {
        await cacheTafsirResources(allResources);
        return allResources;
      }
    } catch (error) {
      logger.warn(
        'Failed to fetch all tafsir resources, trying language-specific approach',
        undefined,
        error as Error
      );
    }

    // Fallback to language-specific fetching
    const languages = ['en', 'ar', 'bn', 'ur', 'id', 'tr', 'fa'];
    const results = await Promise.allSettled(
      languages.map((lang) => fetchResourcesForLanguage(lang))
    );

    // Merge results and deduplicate by ID
    const mergedMap = new Map<number, Tafsir>();
    for (const result of results) {
      if (result.status === 'fulfilled') {
        for (const tafsir of result.value) {
          if (!mergedMap.has(tafsir.id)) {
            mergedMap.set(tafsir.id, tafsir);
          }
        }
      }
    }

    const tafsirs = Array.from(mergedMap.values());
    if (tafsirs.length > 0) {
      await cacheTafsirResources(tafsirs);
    }

    return tafsirs;
  }

  /**
   * Get tafsir resources for a specific language
   */
  async getResourcesByLanguage(language: string): Promise<Tafsir[]> {
    return fetchResourcesForLanguage(language);
  }

  /**
   * Get a specific tafsir by ID
   */
  async getById(id: number): Promise<Tafsir | null> {
    const allResources = await this.getAllResources();
    return allResources.find((t) => t.id === id) || null;
  }

  /**
   * Get tafsir content for a specific verse
   */
  async getTafsirByVerse(verseKey: string, tafsirId: number): Promise<string> {
    try {
      // Try primary API first
      const data = await apiFetch<{ tafsir?: { text: string } }>(
        `tafsirs/${tafsirId}/by_ayah/${encodeURIComponent(verseKey)}`,
        {},
        'Failed to fetch tafsir'
      );
      if (data?.tafsir?.text) {
        return data.tafsir.text;
      }
    } catch (error) {
      logger.warn('Primary tafsir API failed, trying fallback', undefined, error as Error);
    }

    // Fallback to CDN endpoint
    const cdnUrl = `https://api.qurancdn.com/api/qdc/tafsirs/${tafsirId}/by_ayah/${encodeURIComponent(verseKey)}`;
    const data = await apiFetch<{ tafsir?: { text: string } }>(
      cdnUrl,
      {},
      'Failed to fetch tafsir content'
    );
    return data.tafsir?.text || '';
  }

  /**
   * Search tafsir resources by name or language
   */
  async search(searchTerm: string): Promise<Tafsir[]> {
    const allResources = await this.getAllResources();
    return allResources.filter((tafsir) => tafsir.matchesSearch(searchTerm));
  }

  /**
   * Cache tafsir resources for offline access
   */
  async cacheResources(tafsirs: Tafsir[]): Promise<void> {
    await cacheTafsirResources(tafsirs);
  }

  /**
   * Get cached tafsir resources
   */
  async getCachedResources(): Promise<Tafsir[]> {
    return getTafsirCachedResources();
  }
}

