import { readFileSync, writeFileSync } from 'fs';
import path from 'path';

function hexToRgb(hex: string): string {
  const sanitized = hex.replace('#', '');
  const bigint = parseInt(sanitized, 16);
  const r = (bigint >> 16) & 255;
  const g = (bigint >> 8) & 255;
  const b = bigint & 255;
  return `${r} ${g} ${b}`;
}

function kebabCase(str: string): string {
  return str.replace(/([a-z])([A-Z])/g, '$1-$2').toLowerCase();
}

const designSystemPath = path.resolve('design-system.json');
const themePath = path.resolve('app/theme.css');

const design = JSON.parse(readFileSync(designSystemPath, 'utf8'));
const colors = design.colors as Record<string, string>;

const baseKeys = Object.keys(colors).filter((key) => !key.endsWith('Dark'));

const rootVars = baseKeys
  .map((key) => `  --color-${kebabCase(key)}: ${hexToRgb(colors[key])};`)
  .join('\n');

const darkVars = baseKeys
  .map((key) => {
    const darkKey = `${key}Dark`;
    return `  --color-${kebabCase(key)}: ${hexToRgb(colors[darkKey] || colors[key])};`;
  })
  .join('\n');

const css = `:root {\n${rootVars}\n}\n\n.dark {\n${darkVars}\n}\n`;

writeFileSync(themePath, css);
console.log('Generated theme tokens.');

/**
 * Scaffolds a new feature page along with a basic test file.
 *
 * Expects a kebab-case feature name as the first CLI argument and generates:
 * - `app/(features)/<name>/page.tsx`
 * - `app/(features)/<name>/components/.gitkeep`
 * - `app/(features)/<name>/__tests__/<Pascal>Page.test.tsx`
 */

import { mkdir, writeFile } from 'fs/promises';

function toPascalCase(name: string): string {
  return name
    .split(/[-_]/)
    .map((part) => part.charAt(0).toUpperCase() + part.slice(1))
    .join('');
}

async function main(): Promise<void> {
  const name = process.argv[2];
  if (!name) {
    console.error('Usage: ts-node scripts/generateFeature.ts <name>');
    process.exit(1);
  }

  const pascal = toPascalCase(name);
  const dir = `app/(features)/${name}`;
  await mkdir(dir, { recursive: true });
  await Promise.all([
    mkdir(`${dir}/context`, { recursive: true }),
    mkdir(`${dir}/hooks`, { recursive: true }),
    mkdir(`${dir}/lib`, { recursive: true }),
    mkdir(`${dir}/components`, { recursive: true }),
    mkdir(`${dir}/__tests__`, { recursive: true }),
  ]);
  await Promise.all([
    writeFile(`${dir}/context/.gitkeep`, ''),
    writeFile(`${dir}/hooks/.gitkeep`, ''),
    writeFile(`${dir}/lib/.gitkeep`, ''),
    writeFile(`${dir}/components/.gitkeep`, ''),
  ]);

  const page = `// ${dir}/page.tsx
'use client';
import { useEffect, useState } from 'react';
import { useSettings } from '@/app/providers/SettingsContext';
import { getRandomVerse } from '@/lib/api';
import { Verse } from '@/types';

export const ${pascal}Page = () => {
  const { settings } = useSettings();
  const [verse, setVerse] = useState<Verse | null>(null);

  useEffect(() => {
    getRandomVerse(settings.translationId).then(setVerse);
  }, [settings.translationId]);

  if (!verse) return <p>Loading...</p>;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">${pascal}</h1>
      <p>{verse.text_uthmani}</p>
    </div>
  );
};
`;

  await writeFile(`${dir}/page.tsx`, page);

  const test = `import { render, screen } from '@testing-library/react';
import { SettingsProvider } from '@/app/providers/SettingsContext';
import * as api from '@/lib/api';
import { Verse } from '@/types';
import { ${pascal}Page } from '@/app/(features)/${name}/page';

jest.mock('react-i18next', () => ({ useTranslation: () => ({ t: (key: string) => key }) }));
jest.mock('@/lib/api');

const mockVerse: Verse = { id: 1, verse_key: '1:1', text_uthmani: '${name} verse', words: [] } as Verse;

beforeEach(() => {
  (api.getRandomVerse as jest.Mock).mockResolvedValue(mockVerse);
});

test('renders API data', async () => {
  render(
    <SettingsProvider>
      <${pascal}Page />
    </SettingsProvider>
  );
  expect(await screen.findByText('${name} verse')).toBeInTheDocument();
});
`;

  await writeFile(`${dir}/__tests__/${pascal}Page.test.tsx`, test);
  console.log(`Generated feature '${name}' at ${dir}`);
}

main().catch((err) => {
  console.error(err);
  process.exit(1);
});

import { readdirSync, readFileSync } from 'fs';
import path from 'path';

function kebabCase(str: string): string {
  return str.replace(/([a-z])([A-Z])/g, '$1-$2').toLowerCase();
}

const designPath = path.resolve(__dirname, '..', 'design-system.json');
const design = JSON.parse(readFileSync(designPath, 'utf8'));
const allowedTokens = new Set(
  Object.keys(design.colors)
    .filter((key) => !key.endsWith('Dark'))
    .map(kebabCase)
);

const ignore: Record<string, Set<string>> = {
  text: new Set([
    'center',
    'left',
    'right',
    'justify',
    'start',
    'end',
    'xs',
    'sm',
    'base',
    'lg',
    'xl',
    '2xl',
    '3xl',
    '4xl',
    '5xl',
    '6xl',
    '7xl',
    '8xl',
    '9xl',
  ]),
  bg: new Set([
    'cover',
    'contain',
    'center',
    'fixed',
    'local',
    'no-repeat',
    'repeat',
    'repeat-x',
    'repeat-y',
    'auto',
    'bottom',
    'top',
    'left',
    'right',
  ]),
  border: new Set([
    '0',
    '2',
    '4',
    '8',
    't',
    'b',
    'l',
    'r',
    'x',
    'y',
    'solid',
    'dashed',
    'double',
    'collapse',
    'separate',
  ]),
};

const tokenRegex = /(bg|text|border)-([a-zA-Z0-9-]+)/g;

function getFiles(dir: string): string[] {
  const entries = readdirSync(dir, { withFileTypes: true });
  const files: string[] = [];
  for (const entry of entries) {
    const fullPath = path.join(dir, entry.name);
    if (entry.isDirectory()) {
      if (['node_modules', '.git', '.next', 'public'].includes(entry.name)) {
        continue;
      }
      files.push(...getFiles(fullPath));
    } else if (/\.(ts|tsx|js|jsx)$/.test(entry.name)) {
      files.push(fullPath);
    }
  }
  return files;
}

const rootDir = path.resolve(__dirname, '..');
const files = getFiles(rootDir);
const undefinedTokens: Record<string, Set<string>> = {};

for (const file of files) {
  const content = readFileSync(file, 'utf8');
  let match: RegExpExecArray | null;
  while ((match = tokenRegex.exec(content))) {
    const prefix = match[1];
    const token = match[2];
    if (ignore[prefix]?.has(token)) continue;
    if (!allowedTokens.has(token)) {
      const key = `${prefix}-${token}`;
      undefinedTokens[key] ||= new Set();
      undefinedTokens[key].add(path.relative(rootDir, file));
    }
  }
}

if (Object.keys(undefinedTokens).length > 0) {
  console.error('Undefined design tokens found:');
  for (const [token, files] of Object.entries(undefinedTokens)) {
    console.error(`  ${token}`);
    for (const f of files) {
      console.error(`    - ${f}`);
    }
  }
  process.exit(1);
}

console.log('All token classes are defined in design-system.json');
