describe('Verse word-by-word font size', () => {
  beforeAll(() => {
    setMatchMedia(false);
  });
  beforeEach(() => {
    localStorage.clear();
    localStorage.setItem(
      'quranAppSettings',
      JSON.stringify({ arabicFontSize: 40, showByWords: true })
