import { useMemo } from 'react';
import useSWR from 'swr';

import { getTranslations, getWordTranslations } from '@/lib/api';
import { WORD_LANGUAGE_LABELS } from '@/lib/text/wordLanguages';

import type { TranslationResource } from '@/types';

export interface WordLanguageOption {
  name: string;
  id: number;
}

export interface UseTranslationOptionsReturn {
  translationOptions: TranslationResource[];
  wordLanguageOptions: WordLanguageOption[];
  wordLanguageMap: Record<string, number>;
}

export function useTranslationOptions(): UseTranslationOptionsReturn {
  const swrTranslations = useSWR<TranslationResource[]>('translations', getTranslations);
  const translationOptionsData = swrTranslations?.data;
  const translationOptions = useMemo(() => translationOptionsData || [], [translationOptionsData]);

  const swrWord = useSWR('wordTranslations', getWordTranslations);
  const wordTranslationOptionsData = (swrWord as any)?.data as any[] | undefined;
  const wordLanguageMap = useMemo(() => {
    const map: Record<string, number> = {};
    (wordTranslationOptionsData || []).forEach((o: any) => {
      const raw = o?.lang ?? o?.language_name ?? '';
      const name = String(raw).toLowerCase();
      if (!map[name]) {
        map[name] = o.id;
      }
    });
    return map;
  }, [wordTranslationOptionsData]);

  const wordLanguageOptions: WordLanguageOption[] = useMemo(
    () =>
      Object.keys(wordLanguageMap)
        .filter((name) => WORD_LANGUAGE_LABELS[name])
        .map((name) => ({ name: WORD_LANGUAGE_LABELS[name]!, id: wordLanguageMap[name]! })),
    [wordLanguageMap]
  );

  return { translationOptions, wordLanguageOptions, wordLanguageMap } as const;
}

import { useMemo, useRef, useState } from 'react';

import { useInfiniteVerseLoader } from '@/app/(features)/surah/hooks/useInfiniteVerseLoader';
import { useTranslationOptions } from '@/app/(features)/surah/hooks/useTranslationOptions';
import { useSettings } from '@/app/providers/SettingsContext';
import { useAudio } from '@/app/shared/player/context/AudioContext';

import { useNavigationHandlers } from './verse-listing/useNavigationHandlers';
import { getStableTranslationIds } from './verse-listing/utils';

export type {
  UseVerseListingReturn,
  LookupOptions,
  LookupFn,
  UseVerseListingParams,
} from './verse-listing/types';
import type { UseVerseListingParams, UseVerseListingReturn } from './verse-listing/types';

/**
 * Hook for managing verse listing with infinite scroll, audio controls, and settings.
 * Handles verse fetching, translation management, and audio player integration.
 */
export function useVerseListing({
  id,
  lookup,
  initialVerses,
}: UseVerseListingParams): UseVerseListingReturn {
  const [error, setError] = useState<string | null>(null);
  const { settings, setSettings } = useSettings();
  const loadMoreRef = useRef<HTMLDivElement | null>(null);

  const { activeVerse, setActiveVerse, reciter, isPlayerVisible, openPlayer } = useAudio();
  const {
    translationOptions = [],
    wordLanguageOptions = [],
    wordLanguageMap = {},
  } = (useTranslationOptions() as
    | {
        translationOptions?: unknown[];
        wordLanguageOptions?: unknown[];
        wordLanguageMap?: Record<string, unknown>;
      }
    | undefined) ?? {};

  const stableTranslationIds = useMemo(
    () => getStableTranslationIds(settings.translationIds, settings.translationId),
    [settings.translationIds, settings.translationId]
  );

  let {
    verses = [],
    isLoading = false,
    isValidating = false,
    isReachingEnd = false,
  } = (useInfiniteVerseLoader({
    ...(id !== undefined ? { id } : {}),
    lookup,
    stableTranslationIds,
    wordLang: settings.wordLang,
    loadMoreRef,
    error,
    setError: (e: string) => setError(e),
  }) as
    | { verses?: unknown[]; isLoading?: boolean; isValidating?: boolean; isReachingEnd?: boolean }
    | undefined) ?? {};

  if (initialVerses && initialVerses.length) {
    verses = initialVerses as any[];
  }

  const { handleNext, handlePrev } = useNavigationHandlers({
    verses,
    activeVerse,
    setActiveVerse,
    openPlayer,
  });

  return {
    error,
    isLoading,
    verses,
    isValidating,
    isReachingEnd,
    loadMoreRef,
    translationOptions,
    wordLanguageOptions,
    wordLanguageMap,
    settings,
    setSettings,
    activeVerse,
    reciter,
    isPlayerVisible,
    handleNext,
    handlePrev,
  } as const;
}

'use client';

import React, { createContext, useContext, useMemo } from 'react';

import { Settings } from '@/types';

import { usePersistentSettings } from './hooks/usePersistentSettings';
import { ARABIC_FONTS } from './settingsStorage';

// Helper to create setter functions
type SettingsSetters = Pick<
  SettingsContextType,
  | 'setSettings'
  | 'setShowByWords'
  | 'setTajweed'
  | 'setWordLang'
  | 'setWordTranslationId'
  | 'setTafsirIds'
  | 'setTranslationIds'
  | 'setArabicFontSize'
  | 'setTranslationFontSize'
  | 'setTafsirFontSize'
  | 'setArabicFontFace'
>;

const createSetters = (
  dispatch: ReturnType<typeof usePersistentSettings>['dispatch']
): SettingsSetters => ({
  setSettings: (s: Settings): void => dispatch({ type: 'SET_SETTINGS', value: s }),
  setShowByWords: (val: boolean): void => dispatch({ type: 'SET_SHOW_BY_WORDS', value: val }),
  setTajweed: (val: boolean): void => dispatch({ type: 'SET_TAJWEED', value: val }),
  setWordLang: (lang: string): void => dispatch({ type: 'SET_WORD_LANG', value: lang }),
  setWordTranslationId: (id: number): void =>
    dispatch({ type: 'SET_WORD_TRANSLATION_ID', value: id }),
  setTafsirIds: (ids: number[]): void => dispatch({ type: 'SET_TAFSIR_IDS', value: ids }),
  setTranslationIds: (ids: number[]): void => dispatch({ type: 'SET_TRANSLATION_IDS', value: ids }),
  setArabicFontSize: (size: number): void =>
    dispatch({ type: 'SET_ARABIC_FONT_SIZE', value: size }),
  setTranslationFontSize: (size: number): void =>
    dispatch({ type: 'SET_TRANSLATION_FONT_SIZE', value: size }),
  setTafsirFontSize: (size: number): void =>
    dispatch({ type: 'SET_TAFSIR_FONT_SIZE', value: size }),
  setArabicFontFace: (font: string): void =>
    dispatch({ type: 'SET_ARABIC_FONT_FACE', value: font }),
});

interface SettingsContextType {
  settings: Settings;
  setSettings: (settings: Settings) => void;
  arabicFonts: { name: string; value: string; category: string }[];
  setShowByWords: (val: boolean) => void;
  setTajweed: (val: boolean) => void;
  setWordLang: (lang: string) => void;
  setWordTranslationId: (id: number) => void;
  setTafsirIds: (ids: number[]) => void;
  setTranslationIds: (ids: number[]) => void;
  setArabicFontSize: (size: number) => void;
  setTranslationFontSize: (size: number) => void;
  setTafsirFontSize: (size: number) => void;
  setArabicFontFace: (font: string) => void;
}

const SettingsContext = createContext<SettingsContextType | undefined>(undefined);

/**
 * Provides global access to user-configurable settings such as fonts, font sizes,
 * translation and tafsīr selections, word-by-word preferences and tajweed display.
 * Settings are persisted to `localStorage`.
 *
 * Wrap parts of the application that need these values with this provider and use
 * the {@link useSettings} hook to read or update them.
 */
export const SettingsProvider = ({
  children,
}: {
  children: React.ReactNode;
}): React.JSX.Element => {
  const { settings, dispatch } = usePersistentSettings();

  const setters = useMemo(() => createSetters(dispatch), [dispatch]);
  const {
    setSettings,
    setShowByWords,
    setTajweed,
    setWordLang,
    setWordTranslationId,
    setTafsirIds,
    setTranslationIds,
    setArabicFontSize,
    setTranslationFontSize,
    setTafsirFontSize,
    setArabicFontFace,
  } = setters;

  const value = useMemo(
    () => ({
      settings,
      setSettings,
      arabicFonts: ARABIC_FONTS,
      setShowByWords,
      setTajweed,
      setWordLang,
      setWordTranslationId,
      setTafsirIds,
      setTranslationIds,
      setArabicFontSize,
      setTranslationFontSize,
      setTafsirFontSize,
      setArabicFontFace,
    }),
    [
      settings,
      setSettings,
      setShowByWords,
      setTajweed,
      setWordLang,
      setWordTranslationId,
      setTafsirIds,
      setTranslationIds,
      setArabicFontSize,
      setTranslationFontSize,
      setTafsirFontSize,
      setArabicFontFace,
    ]
  );

  return <SettingsContext.Provider value={value}>{children}</SettingsContext.Provider>;
};

export const useSettings = (): SettingsContextType => {
  const ctx = useContext(SettingsContext);
  if (!ctx) throw new Error('useSettings must be used within SettingsProvider');
  return ctx;
};

import { useEffect, useState } from 'react';

import {
  loadBookmarksFromStorage,
  saveBookmarksToStorage,
  loadPinnedFromStorage,
  savePinnedToStorage,
  loadLastReadFromStorage,
  saveLastReadToStorage,
  loadMemorizationFromStorage,
  saveMemorizationToStorage,
} from '@/app/providers/bookmarks/storage-utils';
import * as chaptersApi from '@/lib/api/chapters';

import type { Folder, Bookmark, Chapter, MemorizationPlan } from '@/types';

export function useBookmarkData(): {
  folders: Folder[];
  setFolders: React.Dispatch<React.SetStateAction<Folder[]>>;
  pinnedVerses: Bookmark[];
  setPinnedVerses: React.Dispatch<React.SetStateAction<Bookmark[]>>;
  lastRead: Record<string, number>;
  setLastReadState: React.Dispatch<React.SetStateAction<Record<string, number>>>;
  memorization: Record<string, MemorizationPlan>;
  setMemorizationState: React.Dispatch<React.SetStateAction<Record<string, MemorizationPlan>>>;
  chapters: Chapter[];
} {
  const [folders, setFolders] = useState<Folder[]>([]);
  const [pinnedVerses, setPinnedVerses] = useState<Bookmark[]>([]);
  const [lastRead, setLastReadState] = useState<Record<string, number>>({});
  const [memorization, setMemorizationState] = useState<Record<string, MemorizationPlan>>({});
  const [chapters, setChapters] = useState<Chapter[]>([]);

  useEffect(() => {
    const fetchChaptersCandidate =
      (typeof (chaptersApi as any).getChapters === 'function'
        ? (chaptersApi as any).getChapters
        : (chaptersApi as any).default) || null;

    if (typeof fetchChaptersCandidate === 'function') {
      try {
        const result = (fetchChaptersCandidate as () => Promise<Chapter[]> | Chapter[])();
        if (result && typeof (result as any).then === 'function') {
          void (result as Promise<Chapter[]>)
            .then((chs: Chapter[]) => setChapters(chs ?? []))
            .catch(() => {});
        } else if (Array.isArray(result)) {
          setChapters(result);
        }
      } catch {
        // ignore fetch errors in tests
      }
    }
    setFolders(loadBookmarksFromStorage());
    setPinnedVerses(loadPinnedFromStorage());
    setLastReadState(loadLastReadFromStorage());
    setMemorizationState(loadMemorizationFromStorage());
  }, []);

  useEffect(() => saveBookmarksToStorage(folders), [folders]);
  useEffect(() => savePinnedToStorage(pinnedVerses), [pinnedVerses]);
  useEffect(() => saveLastReadToStorage(lastRead), [lastRead]);
  useEffect(() => saveMemorizationToStorage(memorization), [memorization]);

  return {
    folders,
    setFolders,
    pinnedVerses,
    setPinnedVerses,
    lastRead,
    setLastReadState,
    memorization,
    setMemorizationState,
    chapters,
  } as const;
}

import { Settings } from '@/types';

export type Action =
  | { type: 'SET_SETTINGS'; value: Settings }
  | { type: 'SET_SHOW_BY_WORDS'; value: boolean }
  | { type: 'SET_TAJWEED'; value: boolean }
  | { type: 'SET_WORD_LANG'; value: string }
  | { type: 'SET_WORD_TRANSLATION_ID'; value: number }
  | { type: 'SET_TAFSIR_IDS'; value: number[] }
  | { type: 'SET_TRANSLATION_IDS'; value: number[] }
  | { type: 'SET_ARABIC_FONT_SIZE'; value: number }
  | { type: 'SET_TRANSLATION_FONT_SIZE'; value: number }
  | { type: 'SET_TAFSIR_FONT_SIZE'; value: number }
  | { type: 'SET_ARABIC_FONT_FACE'; value: string };

export const reducer = (state: Settings, action: Action): Settings => {
  switch (action.type) {
    case 'SET_SETTINGS':
      return action.value;
    case 'SET_SHOW_BY_WORDS':
      return { ...state, showByWords: action.value };
    case 'SET_TAJWEED':
      return { ...state, tajweed: action.value };
    case 'SET_WORD_LANG':
      return { ...state, wordLang: action.value };
    case 'SET_WORD_TRANSLATION_ID':
      return { ...state, wordTranslationId: action.value };
    case 'SET_TAFSIR_IDS':
      return { ...state, tafsirIds: action.value };
    case 'SET_TRANSLATION_IDS':
      return {
        ...state,
        translationIds: action.value,
        translationId: action.value.length > 0 ? action.value[0] : state.translationId,
      };
    case 'SET_ARABIC_FONT_SIZE':
      return { ...state, arabicFontSize: action.value };
    case 'SET_TRANSLATION_FONT_SIZE':
      return { ...state, translationFontSize: action.value };
    case 'SET_TAFSIR_FONT_SIZE':
      return { ...state, tafsirFontSize: action.value };
    case 'SET_ARABIC_FONT_FACE':
      return { ...state, arabicFontFace: action.value };
    default:
      return state;
  }
};

import { screen, fireEvent, waitFor, act } from '@testing-library/react';
import userEvent from '@testing-library/user-event';

import {
  renderResponsiveVerseActions,
  rerenderResponsiveVerseActions,
} from '@/app/testUtils/responsiveVerseActionsTestUtils';

describe('ResponsiveVerseActions interactions', () => {
  it('should handle play button clicks', async () => {
    const onPlayPause = jest.fn();
    renderResponsiveVerseActions({ onPlayPause });

    const playButton = await screen.findByRole('button', { name: /play/i });
    await act(async () => {
      fireEvent.click(playButton);
    });

    await waitFor(() => {
      expect(onPlayPause).toHaveBeenCalledTimes(1);
    });
  });

  it('should handle bookmark toggle', async () => {
    const onBookmark = jest.fn();
    renderResponsiveVerseActions({ onBookmark, showRemove: true, isBookmarked: true });

    const bookmarkButton = await screen.findByRole('button', { name: /remove bookmark/i });
    await act(async () => {
      fireEvent.click(bookmarkButton);
    });

    await waitFor(() => {
      expect(onBookmark).toHaveBeenCalledTimes(1);
    });
  });

  it('should show correct play/pause state', async () => {
    const { rerender } = renderResponsiveVerseActions();

    await screen.findByRole('button', { name: /play/i });

    await act(async () => {
      rerenderResponsiveVerseActions(rerender, { isPlaying: true });
    });

    await screen.findByRole('button', { name: /pause/i });
  });

  it('toggles play and pause on user interaction', async () => {
    const onPlayPause = jest.fn();
    const { rerender } = renderResponsiveVerseActions({ onPlayPause });

    const user = userEvent.setup();
    const playButton = await screen.findByRole('button', { name: /play/i });
    await user.click(playButton);
    expect(onPlayPause).toHaveBeenCalledTimes(1);

    rerenderResponsiveVerseActions(rerender, { isPlaying: true, onPlayPause });
    const pauseButton = await screen.findByRole('button', { name: /pause/i });
    await user.click(pauseButton);
    expect(onPlayPause).toHaveBeenCalledTimes(2);
  });

  it('should show loading state', async () => {
    renderResponsiveVerseActions({ isLoadingAudio: true });

    await screen.findByLabelText(/loading/i, { selector: 'svg', hidden: true });
  });

  it('should show bookmarked state', async () => {
    renderResponsiveVerseActions({ isBookmarked: true });

    await screen.findByRole('button', { name: /remove bookmark/i });
  });

  it('should support keyboard navigation', async () => {
    renderResponsiveVerseActions();

    const playButton = await screen.findByRole('button', { name: /play/i });
    await screen.findByRole('button', { name: /bookmark/i });

    playButton.focus();
    expect(document.activeElement).toBe(playButton);

    const user = userEvent.setup();
    await user.tab();
    await waitFor(() => {
      expect(document.activeElement).not.toBe(playButton);
    });
  });
});

