import { useEffect, useCallback, useRef } from 'react';

import { ILogger } from '@/src/domain/interfaces/ILogger';
import { logger } from '@/src/infrastructure/monitoring/Logger';

interface PrefetchOptions {
  maxCacheSize?: number;
  prefetchNext?: boolean;
  prefetchPrevious?: boolean;
  enabled?: boolean;
}

interface PrefetchedAudio {
  url: string;
  blob?: Blob;
  objectUrl?: string;
  timestamp: number;
  size: number;
}

/**
 * Hook for prefetching audio segments to enable instant playback
 * Implements intelligent caching with size limits and cleanup
 */
export function useAudioPrefetch(
  currentAudioUrl: string | null,
  getNextAudioUrl?: () => string | null,
  getPreviousAudioUrl?: () => string | null,
  options: PrefetchOptions = {}
): {
  prefetchAudio: (url: string) => Promise<string | null>;
  getPrefetchedUrl: (url: string) => string | null;
  clearCache: () => void;
  getCacheStats: () => { count: number; totalSize: number };
} {
  const {
    maxCacheSize = 50 * 1024 * 1024, // 50MB default cache size
    prefetchNext = true,
    prefetchPrevious = false,
    enabled = true,
  } = options;

  const cacheRef = useRef<Map<string, PrefetchedAudio>>(new Map());
  const prefetchingRef = useRef<Set<string>>(new Set());
  const loggerRef = useRef<ILogger>(logger);

  const cleanupOldCache = useCallback(() => {
    const cache = cacheRef.current;
    let totalSize = 0;
    const entries = Array.from(cache.entries());

    // Calculate total size
    for (const [, audio] of entries) {
      totalSize += audio.size;
    }

    // Remove oldest entries if over size limit
    if (totalSize > maxCacheSize) {
      entries.sort((a, b) => a[1].timestamp - b[1].timestamp);

      while (totalSize > maxCacheSize * 0.8 && entries.length > 0) {
        const [url, audio] = entries.shift()!;
        totalSize -= audio.size;

        // Clean up object URL
        if (audio.objectUrl) {
          URL.revokeObjectURL(audio.objectUrl);
        }

        cache.delete(url);
        loggerRef.current.debug('Audio cache cleanup', {
          removedUrl: url,
          newTotalSize: totalSize,
        });
      }
    }
  }, [maxCacheSize]);

  const prefetchAudio = useCallback(
    async (url: string): Promise<string | null> => {
      if (!enabled || !url || cacheRef.current.has(url) || prefetchingRef.current.has(url)) {
        return cacheRef.current.get(url)?.objectUrl || null;
      }

      prefetchingRef.current.add(url);

      try {
        loggerRef.current.debug('Prefetching audio', { url });

        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 30000); // 30s timeout

        const response = await fetch(url, {
          signal: controller.signal,
          headers: {
            Range: 'bytes=0-1024', // Prefetch first 1KB for instant start
          },
        });

        clearTimeout(timeoutId);

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}`);
        }

        const blob = await response.blob();
        const objectUrl = URL.createObjectURL(blob);

        const prefetchedAudio: PrefetchedAudio = {
          url,
          blob,
          objectUrl,
          timestamp: Date.now(),
          size: blob.size,
        };

        cacheRef.current.set(url, prefetchedAudio);
        cleanupOldCache();

        loggerRef.current.debug('Audio prefetched successfully', {
          url,
          size: blob.size,
          cacheSize: cacheRef.current.size,
        });

        return objectUrl;
      } catch (error) {
        loggerRef.current.warn('Audio prefetch failed', {
          url,
          error: error instanceof Error ? error.message : String(error),
        });
        return null;
      } finally {
        prefetchingRef.current.delete(url);
      }
    },
    [enabled, cleanupOldCache]
  );

  const getPrefetchedUrl = useCallback((url: string): string | null => {
    return cacheRef.current.get(url)?.objectUrl || null;
  }, []);

  const clearCache = useCallback(() => {
    for (const [, audio] of cacheRef.current) {
      if (audio.objectUrl) {
        URL.revokeObjectURL(audio.objectUrl);
      }
    }
    cacheRef.current.clear();
    loggerRef.current.debug('Audio cache cleared');
  }, []);

  const getCacheStats = useCallback(() => {
    let totalSize = 0;
    for (const [, audio] of cacheRef.current) {
      totalSize += audio.size;
    }
    return {
      count: cacheRef.current.size,
      totalSize,
    };
  }, []);

  // Prefetch adjacent audio files when current audio changes
  useEffect(() => {
    if (!enabled || !currentAudioUrl) return;

    const prefetchTasks: Promise<void>[] = [];

    // Prefetch next audio
    if (prefetchNext && getNextAudioUrl) {
      const nextUrl = getNextAudioUrl();
      if (nextUrl) {
        prefetchTasks.push(
          prefetchAudio(nextUrl)
            .then(() => {
              loggerRef.current.debug('Next audio prefetched', { nextUrl });
            })
            .catch(() => {
              // Silently handle prefetch failures
            })
        );
      }
    }

    // Prefetch previous audio
    if (prefetchPrevious && getPreviousAudioUrl) {
      const previousUrl = getPreviousAudioUrl();
      if (previousUrl) {
        prefetchTasks.push(
          prefetchAudio(previousUrl)
            .then(() => {
              loggerRef.current.debug('Previous audio prefetched', { previousUrl });
            })
            .catch(() => {
              // Silently handle prefetch failures
            })
        );
      }
    }

    // Execute prefetch tasks
    Promise.allSettled(prefetchTasks);
  }, [
    currentAudioUrl,
    enabled,
    prefetchNext,
    prefetchPrevious,
    getNextAudioUrl,
    getPreviousAudioUrl,
    prefetchAudio,
  ]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      clearCache();
    };
  }, [clearCache]);

  return {
    prefetchAudio,
    getPrefetchedUrl,
    clearCache,
    getCacheStats,
  };
}

import { ILogger } from '@/src/domain/interfaces/ILogger';
import { logger } from '@/src/infrastructure/monitoring/Logger';

interface AudioSegment {
  url: string;
  start: number;
  end: number;
  priority: 'high' | 'medium' | 'low';
}

interface PrefetchResult {
  url: string;
  success: boolean;
  size: number;
  duration: number;
}

/**
 * Audio segment prefetching service for instant play experience
 * Implements intelligent prefetching of small audio segments
 */
export class AudioSegmentPrefetch {
  private readonly logger: ILogger;
  private readonly cache = new Map<string, ArrayBuffer>();
  private readonly prefetchQueue = new Set<string>();
  private readonly maxCacheSize: number;
  private readonly segmentSize: number;

  constructor(
    options: {
      maxCacheSize?: number;
      segmentSize?: number;
    } = {}
  ) {
    this.logger = logger;
    this.maxCacheSize = options.maxCacheSize || 10 * 1024 * 1024; // 10MB
    this.segmentSize = options.segmentSize || 1024 * 1024; // 1MB segments
  }

  /**
   * Prefetch the beginning of an audio file for instant playback
   */
  async prefetchAudioStart(
    url: string,
    options: {
      priority?: 'high' | 'medium' | 'low';
      bytes?: number;
    } = {}
  ): Promise<boolean> {
    const { priority = 'medium', bytes = this.segmentSize } = options;

    if (this.cache.has(url) || this.prefetchQueue.has(url)) {
      return true;
    }

    this.prefetchQueue.add(url);

    try {
      this.logger.debug('Prefetching audio start segment', { url, bytes, priority });

      const startTime = performance.now();

      // Fetch first segment with range request
      const response = await fetch(url, {
        headers: {
          Range: `bytes=0-${bytes - 1}`,
        },
        signal: AbortSignal.timeout(10000), // 10s timeout
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const arrayBuffer = await response.arrayBuffer();
      const duration = performance.now() - startTime;

      // Check cache size and cleanup if needed
      await this.ensureCacheSpace(arrayBuffer.byteLength);

      // Store in cache
      this.cache.set(url, arrayBuffer);

      this.logger.info('Audio segment prefetched successfully', {
        url,
        size: arrayBuffer.byteLength,
        duration,
        cacheSize: this.getCacheSize(),
      });

      return true;
    } catch (error) {
      this.logger.warn('Failed to prefetch audio segment', {
        url,
        error: error instanceof Error ? error.message : String(error),
      });
      return false;
    } finally {
      this.prefetchQueue.delete(url);
    }
  }

  /**
   * Prefetch multiple audio segments with priority handling
   */
  async prefetchAudioList(
    audioItems: Array<{
      url: string;
      priority?: 'high' | 'medium' | 'low';
    }>,
    options: {
      maxConcurrent?: number;
      delayBetween?: number;
    } = {}
  ): Promise<PrefetchResult[]> {
    const { maxConcurrent = 3, delayBetween = 100 } = options;

    // Sort by priority: high -> medium -> low
    const priorityOrder = { high: 0, medium: 1, low: 2 };
    const sortedItems = audioItems.sort((a, b) => {
      const aPriority = priorityOrder[a.priority || 'medium'];
      const bPriority = priorityOrder[b.priority || 'medium'];
      return aPriority - bPriority;
    });

    this.logger.info('Starting batch audio prefetch', {
      total: sortedItems.length,
      maxConcurrent,
      priorityDistribution: this.getPriorityDistribution(sortedItems),
    });

    const results: PrefetchResult[] = [];

    // Process in batches
    for (let i = 0; i < sortedItems.length; i += maxConcurrent) {
      const batch = sortedItems.slice(i, i + maxConcurrent);

      const batchPromises = batch.map(async (item) => {
        const startTime = performance.now();
        const success = await this.prefetchAudioStart(item.url, {
          priority: item.priority,
        });
        const duration = performance.now() - startTime;

        return {
          url: item.url,
          success,
          size: success ? this.cache.get(item.url)?.byteLength || 0 : 0,
          duration,
        };
      });

      const batchResults = await Promise.allSettled(batchPromises);

      // Collect results
      batchResults.forEach((result, index) => {
        if (result.status === 'fulfilled') {
          results.push(result.value);
        } else {
          results.push({
            url: batch[index].url,
            success: false,
            size: 0,
            duration: 0,
          });
        }
      });

      // Add delay between batches to avoid overwhelming the network
      if (i + maxConcurrent < sortedItems.length && delayBetween > 0) {
        await new Promise((resolve) => setTimeout(resolve, delayBetween));
      }
    }

    const successCount = results.filter((r) => r.success).length;
    this.logger.info('Batch audio prefetch completed', {
      successful: successCount,
      failed: results.length - successCount,
      totalSize: results.reduce((sum, r) => sum + r.size, 0),
      cacheSize: this.getCacheSize(),
    });

    return results;
  }

  /**
   * Get prefetched audio data
   */
  getPrefetchedAudio(url: string): ArrayBuffer | null {
    return this.cache.get(url) || null;
  }

  /**
   * Check if audio is prefetched
   */
  isPrefetched(url: string): boolean {
    return this.cache.has(url);
  }

  /**
   * Clear specific audio from cache
   */
  clearAudio(url: string): boolean {
    const deleted = this.cache.delete(url);
    if (deleted) {
      this.logger.debug('Audio removed from prefetch cache', { url });
    }
    return deleted;
  }

  /**
   * Clear entire prefetch cache
   */
  clearCache(): void {
    const size = this.getCacheSize();
    this.cache.clear();
    this.logger.info('Prefetch cache cleared', { clearedSize: size });
  }

  /**
   * Get cache statistics
   */
  getCacheStats(): {
    entries: number;
    totalSize: number;
    averageSize: number;
    urls: string[];
  } {
    const entries = this.cache.size;
    const totalSize = this.getCacheSize();
    const averageSize = entries > 0 ? totalSize / entries : 0;
    const urls = Array.from(this.cache.keys());

    return {
      entries,
      totalSize,
      averageSize,
      urls,
    };
  }

  /**
   * Create blob URL from prefetched data for instant playback
   */
  createBlobUrl(url: string): string | null {
    const data = this.cache.get(url);
    if (!data) {
      return null;
    }

    try {
      const blob = new Blob([data], { type: 'audio/mpeg' });
      const blobUrl = URL.createObjectURL(blob);

      this.logger.debug('Created blob URL for prefetched audio', {
        originalUrl: url,
        blobUrl,
        size: data.byteLength,
      });

      return blobUrl;
    } catch (error) {
      this.logger.error('Failed to create blob URL', {
        url,
        error: error instanceof Error ? error.message : String(error),
      });
      return null;
    }
  }

  /**
   * Preload audio for immediate next/previous navigation
   */
  async preloadNavigation(
    currentUrl: string,
    getNextUrl: () => string | null,
    getPreviousUrl: () => string | null
  ): Promise<void> {
    const navigationUrls: Array<{ url: string; priority: 'high' | 'medium' }> = [];

    // High priority for next (user likely to play next)
    const nextUrl = getNextUrl();
    if (nextUrl && nextUrl !== currentUrl) {
      navigationUrls.push({ url: nextUrl, priority: 'high' });
    }

    // Medium priority for previous (less likely but still useful)
    const prevUrl = getPreviousUrl();
    if (prevUrl && prevUrl !== currentUrl) {
      navigationUrls.push({ url: prevUrl, priority: 'medium' });
    }

    if (navigationUrls.length > 0) {
      this.logger.debug('Preloading navigation audio', {
        next: nextUrl,
        previous: prevUrl,
        count: navigationUrls.length,
      });

      await this.prefetchAudioList(navigationUrls, {
        maxConcurrent: 2,
        delayBetween: 0, // No delay for navigation preload
      });
    }
  }

  /**
   * Ensure cache doesn't exceed size limit
   */
  private async ensureCacheSpace(newItemSize: number): Promise<void> {
    const currentSize = this.getCacheSize();

    if (currentSize + newItemSize > this.maxCacheSize) {
      // Remove oldest entries until we have space
      const entries = Array.from(this.cache.entries());
      let removedSize = 0;

      while (removedSize < newItemSize && entries.length > 0) {
        const [url, data] = entries.shift()!;
        this.cache.delete(url);
        removedSize += data.byteLength;
      }

      this.logger.debug('Cache cleanup completed', {
        removedSize,
        remainingSize: this.getCacheSize(),
        spaceNeeded: newItemSize,
      });
    }
  }

  /**
   * Get total cache size in bytes
   */
  private getCacheSize(): number {
    let totalSize = 0;
    for (const data of this.cache.values()) {
      totalSize += data.byteLength;
    }
    return totalSize;
  }

  /**
   * Get priority distribution for logging
   */
  private getPriorityDistribution(
    items: Array<{ priority?: 'high' | 'medium' | 'low' }>
  ): Record<string, number> {
    return items.reduce(
      (acc, item) => {
        const priority = item.priority || 'medium';
        acc[priority] = (acc[priority] || 0) + 1;
        return acc;
      },
      {} as Record<string, number>
    );
  }
}

// Export singleton instance
export const audioSegmentPrefetch = new AudioSegmentPrefetch();

import { ILogger } from '@/src/domain/interfaces/ILogger';
import { logger } from '@/src/infrastructure/monitoring/Logger';

interface AudioCacheOptions {
  cacheName?: string;
  maxAge?: number;
  maxEntries?: number;
}

/**
 * Service Worker utilities for audio caching
 * Provides programmatic control over PWA audio cache
 */
export class ServiceWorkerAudioCache {
  private readonly logger: ILogger;
  private readonly cacheName: string;
  private readonly maxAge: number;
  private readonly maxEntries: number;

  constructor(options: AudioCacheOptions = {}) {
    this.logger = logger;
    this.cacheName = options.cacheName || 'quran-audio-cache-v1';
    this.maxAge = options.maxAge || 7 * 24 * 60 * 60 * 1000; // 7 days
    this.maxEntries = options.maxEntries || 50;
  }

  /**
   * Check if service worker and Cache API are available
   */
  private isSupported(): boolean {
    return typeof window !== 'undefined' && 'serviceWorker' in navigator && 'caches' in window;
  }

  /**
   * Preload audio file into cache for offline access
   */
  async preloadAudio(url: string): Promise<boolean> {
    if (!this.isSupported()) {
      this.logger.debug('Service worker or Cache API not supported');
      return false;
    }

    try {
      const cache = await caches.open(this.cacheName);

      // Check if already cached
      const cached = await cache.match(url);
      if (cached) {
        this.logger.debug('Audio already cached', { url });
        return true;
      }

      // Preload with range request for better streaming support
      const response = await fetch(url, {
        headers: {
          Range: 'bytes=0-',
        },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch audio: ${response.status}`);
      }

      await cache.put(url, response);
      await this.cleanupOldEntries();

      this.logger.info('Audio preloaded successfully', { url });
      return true;
    } catch (error) {
      this.logger.error('Failed to preload audio', {
        url,
        error: error instanceof Error ? error.message : String(error),
      });
      return false;
    }
  }

  /**
   * Check if audio is cached
   */
  async isCached(url: string): Promise<boolean> {
    if (!this.isSupported()) return false;

    try {
      const cache = await caches.open(this.cacheName);
      const response = await cache.match(url);
      return response !== undefined;
    } catch (error) {
      this.logger.warn('Failed to check cache', {
        url,
        error: error instanceof Error ? error.message : String(error),
      });
      return false;
    }
  }

  /**
   * Remove specific audio from cache
   */
  async removeFromCache(url: string): Promise<boolean> {
    if (!this.isSupported()) return false;

    try {
      const cache = await caches.open(this.cacheName);
      const deleted = await cache.delete(url);

      if (deleted) {
        this.logger.debug('Audio removed from cache', { url });
      }

      return deleted;
    } catch (error) {
      this.logger.warn('Failed to remove from cache', {
        url,
        error: error instanceof Error ? error.message : String(error),
      });
      return false;
    }
  }

  /**
   * Get cache statistics
   */
  async getCacheStats(): Promise<{
    count: number;
    totalSize: number;
    entries: Array<{ url: string; size: number; timestamp: number }>;
  }> {
    if (!this.isSupported()) {
      return { count: 0, totalSize: 0, entries: [] };
    }

    try {
      const cache = await caches.open(this.cacheName);
      const keys = await cache.keys();
      const entries: Array<{ url: string; size: number; timestamp: number }> = [];
      let totalSize = 0;

      for (const request of keys) {
        try {
          const response = await cache.match(request);
          if (response) {
            const size = await this.getResponseSize(response);
            const timestamp = this.getResponseTimestamp(response);

            entries.push({
              url: request.url,
              size,
              timestamp,
            });

            totalSize += size;
          }
        } catch (error) {
          // Skip entries that can't be read
          this.logger.debug('Failed to read cache entry', { url: request.url });
        }
      }

      return { count: entries.length, totalSize, entries };
    } catch (error) {
      this.logger.warn('Failed to get cache stats', {
        error: error instanceof Error ? error.message : String(error),
      });
      return { count: 0, totalSize: 0, entries: [] };
    }
  }

  /**
   * Clear entire audio cache
   */
  async clearCache(): Promise<boolean> {
    if (!this.isSupported()) return false;

    try {
      const deleted = await caches.delete(this.cacheName);

      if (deleted) {
        this.logger.info('Audio cache cleared');
      }

      return deleted;
    } catch (error) {
      this.logger.error('Failed to clear cache', {
        error: error instanceof Error ? error.message : String(error),
      });
      return false;
    }
  }

  /**
   * Cleanup old entries based on age and count limits
   */
  private async cleanupOldEntries(): Promise<void> {
    try {
      const stats = await this.getCacheStats();
      const now = Date.now();
      const cache = await caches.open(this.cacheName);

      // Sort by timestamp (oldest first)
      const sortedEntries = stats.entries.sort((a, b) => a.timestamp - b.timestamp);

      // Remove entries that are too old
      const oldEntries = sortedEntries.filter((entry) => now - entry.timestamp > this.maxAge);

      // Remove excess entries if over limit
      const excessCount = Math.max(0, sortedEntries.length - this.maxEntries);
      const entriesToRemove = [...oldEntries];

      if (excessCount > 0) {
        entriesToRemove.push(...sortedEntries.slice(0, excessCount));
      }

      // Remove duplicate URLs and delete from cache
      const urlsToRemove = [...new Set(entriesToRemove.map((entry) => entry.url))];

      for (const url of urlsToRemove) {
        await cache.delete(url);
      }

      if (urlsToRemove.length > 0) {
        this.logger.debug('Cache cleanup completed', {
          removedCount: urlsToRemove.length,
          reason: oldEntries.length > 0 ? 'age and count limits' : 'count limit',
        });
      }
    } catch (error) {
      this.logger.warn('Cache cleanup failed', {
        error: error instanceof Error ? error.message : String(error),
      });
    }
  }

  /**
   * Get response size (approximate)
   */
  private async getResponseSize(response: Response): Promise<number> {
    try {
      const contentLength = response.headers.get('content-length');
      if (contentLength) {
        return parseInt(contentLength, 10);
      }

      // Fallback: clone and read blob size
      const clone = response.clone();
      const blob = await clone.blob();
      return blob.size;
    } catch {
      return 0;
    }
  }

  /**
   * Get response timestamp from headers or current time
   */
  private getResponseTimestamp(response: Response): number {
    const dateHeader = response.headers.get('date');
    if (dateHeader) {
      const date = new Date(dateHeader);
      if (!isNaN(date.getTime())) {
        return date.getTime();
      }
    }
    return Date.now();
  }

  /**
   * Preload a list of audio URLs with concurrency control
   */
  async preloadAudioList(
    urls: string[],
    options: { concurrency?: number; priority?: 'high' | 'low' } = {}
  ): Promise<{ successful: string[]; failed: string[] }> {
    const { concurrency = 3, priority = 'low' } = options;
    const successful: string[] = [];
    const failed: string[] = [];

    this.logger.info('Starting batch audio preload', { count: urls.length, concurrency });

    // Process URLs in batches
    for (let i = 0; i < urls.length; i += concurrency) {
      const batch = urls.slice(i, i + concurrency);
      const batchPromises = batch.map(async (url) => {
        // Add delay for low priority to not block other requests
        if (priority === 'low') {
          await new Promise((resolve) => setTimeout(resolve, 100));
        }

        const success = await this.preloadAudio(url);
        if (success) {
          successful.push(url);
        } else {
          failed.push(url);
        }
      });

      await Promise.allSettled(batchPromises);
    }

    this.logger.info('Batch audio preload completed', {
      successful: successful.length,
      failed: failed.length,
      total: urls.length,
    });

    return { successful, failed };
  }
}

// Export singleton instance
export const audioCache = new ServiceWorkerAudioCache();

/**
 * Presentation layer logging service
 * This service provides standardized logging for the app layer while abstracting infrastructure dependencies
 */

import { loggerAdapter } from '@/src/infrastructure/monitoring/LoggerAdapter';

/**
 * Standardized logging service for the app layer
 * Provides consistent logging interface with context awareness
 */
export class LoggingService {
  private static instance: LoggingService;

  private constructor() {}

  static getInstance(): LoggingService {
    if (!LoggingService.instance) {
      LoggingService.instance = new LoggingService();
    }
    return LoggingService.instance;
  }

  /**
   * Log informational messages
   */
  info(message: string, context?: Record<string, unknown>, error?: Error): void {
    loggerAdapter.info(this.formatMessage(message), this.enhanceContext(context), error);
  }

  /**
   * Log warning messages
   */
  warn(message: string, context?: Record<string, unknown>, error?: Error): void {
    loggerAdapter.warn(this.formatMessage(message), this.enhanceContext(context), error);
  }

  /**
   * Log error messages
   */
  error(message: string, context?: Record<string, unknown>, error?: Error): void {
    loggerAdapter.error(this.formatMessage(message), this.enhanceContext(context), error);
  }

  /**
   * Log debug messages (only in development)
   */
  debug(message: string, context?: Record<string, unknown>, error?: Error): void {
    if (process.env.NODE_ENV === 'development') {
      loggerAdapter.debug(this.formatMessage(message), this.enhanceContext(context), error);
    }
  }

  /**
   * Log user interactions for analytics
   */
  logUserAction(action: string, feature: string, metadata?: Record<string, unknown>): void {
    this.info(`User action: ${action}`, {
      feature,
      action,
      ...metadata,
      type: 'user_interaction',
    });
  }

  /**
   * Log performance metrics
   */
  logPerformance(
    metric: string,
    value: number,
    unit: string,
    context?: Record<string, unknown>
  ): void {
    this.info(`Performance metric: ${metric}`, {
      metric,
      value,
      unit,
      ...context,
      type: 'performance',
    });
  }

  /**
   * Log API calls and responses
   */
  logApiCall(method: string, url: string, status: number, duration?: number, error?: Error): void {
    const logMethod = status >= 400 ? this.error : this.info;
    logMethod.call(
      this,
      `API ${method} ${url} - ${status}`,
      {
        method,
        url,
        status,
        duration,
        type: 'api_call',
      },
      error
    );
  }

  /**
   * Format message with consistent prefix
   */
  private formatMessage(message: string): string {
    return `[App] ${message}`;
  }

  /**
   * Enhance context with common app-level information
   */
  private enhanceContext(context?: Record<string, unknown>): Record<string, unknown> {
    return {
      layer: 'app',
      timestamp: new Date().toISOString(),
      url: typeof window !== 'undefined' ? window.location.href : undefined,
      userAgent: typeof window !== 'undefined' ? navigator.userAgent : undefined,
      ...context,
    };
  }
}

